import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend, BarChart, Bar } from 'recharts';
import '../Dashboard.css';
import ActivityHeatmap from '../ActivityHeatmap';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const Dashboard = ({ user }) => {
  const { username } = useParams();
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [recentEntries, setRecentEntries] = useState([]);
  const [moodTrends7, setMoodTrends7] = useState([]);
  const [moodTrends180, setMoodTrends180] = useState([]);
  const [activityData, setActivityData] = useState([]);
  const [calendarStreaks, setCalendarStreaks] = useState([]);
  const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth());
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());

  const API_BASE_URL = 'http://localhost:5001/api';

  // Fetch dashboard data
  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      
      // Fetch dashboard stats
      const statsResponse = await fetch(`${API_BASE_URL}/user/dashboard`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Fetch recent entries
      const entriesResponse = await fetch(`${API_BASE_URL}/journal?limit=5&sortBy=date&sortOrder=desc`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (statsResponse.ok && entriesResponse.ok) {
        const statsData = await statsResponse.json();
        const entriesData = await entriesResponse.json();
        
        setDashboardData(statsData.data);
        setRecentEntries(entriesData.data.entries);
      } else {
        setError('Failed to fetch dashboard data');
      }
    } catch (error) {
      setError('Network error');
    } finally {
      setLoading(false);
    }
  };

  // Fetch mood trends and activity heatmap data
  useEffect(() => {
    const token = localStorage.getItem('token');
    // Fetch 7-day mood trends
    fetch(`${API_BASE_URL}/insights/mood?period=7`, {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setMoodTrends7(data.data?.intensityTrends || []));
    // Fetch 6-month mood trends
    fetch(`${API_BASE_URL}/insights/mood?period=180`, {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setMoodTrends180(data.data?.intensityTrends || []));
    // Fetch activity heatmap (past year)
    fetch(`${API_BASE_URL}/insights/patterns?period=365`, {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setActivityData(data.data?.entries || []));
    // Fetch calendar streaks (reuse activity data for now)
    // You can enhance this to highlight streaks specifically
    fetch(`${API_BASE_URL}/insights/patterns?period=365`, {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setCalendarStreaks(data.data?.entries || []));
  }, []);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 to-green-50 flex items-center justify-center">
        <div className="spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 to-green-50 p-6">
        <div className="max-w-6xl mx-auto">
          <div className="error-message">
            <h2 className="text-xl font-semibold mb-2">‚ùå Error Loading Dashboard</h2>
            <p>{error}</p>
            <button onClick={fetchDashboardData} className="btn-primary mt-4">
              üîÑ Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  const getLevelInfo = (level) => {
    const levels = {
      1: { name: 'Novice Journaler', color: '#6b7280', icon: 'üå±' },
      2: { name: 'Mindful Writer', color: '#10b981', icon: 'üìù' },
      3: { name: 'Emotional Explorer', color: '#3b82f6', icon: 'üîç' },
      4: { name: 'Wellness Warrior', color: '#8b5cf6', icon: '‚öîÔ∏è' },
      5: { name: 'Sage of Self', color: '#f59e0b', icon: 'üßò' },
      6: { name: 'Master of Mindfulness', color: '#ec4899', icon: 'üåü' }
    };
    return levels[level] || levels[1];
  };

  const getMoodEmoji = (mood) => {
    const emojis = {
      happy: 'üòä',
      sad: 'üò¢',
      excited: 'ü§©',
      calm: 'üòå',
      anxious: 'üò∞',
      joyful: 'üòÑ',
      tired: 'üò¥',
      neutral: 'üòê'
    };
    return emojis[mood] || 'üòê';
  };

  const levelInfo = getLevelInfo(dashboardData?.level || 1);

  // Example mood and happiness data (replace with backend data)
  const moodData = dashboardData?.moodTrends || [
    { date: 'Mon', mood: 3 },
    { date: 'Tue', mood: 4 },
    { date: 'Wed', mood: 2 },
    { date: 'Thu', mood: 5 },
    { date: 'Fri', mood: 4 },
    { date: 'Sat', mood: 3 },
    { date: 'Sun', mood: 5 },
  ];
  const happinessData = dashboardData?.happinessTrends || [
    { date: 'Mon', happiness: 60 },
    { date: 'Tue', happiness: 70 },
    { date: 'Wed', happiness: 50 },
    { date: 'Thu', happiness: 80 },
    { date: 'Fri', happiness: 75 },
    { date: 'Sat', happiness: 65 },
    { date: 'Sun', happiness: 90 },
  ];

  // Filter activityData for selected month/year
  const filteredActivityData = activityData.filter(entry => {
    const date = new Date(entry.createdAt);
    return date.getMonth() === selectedMonth && date.getFullYear() === selectedYear;
  });

  return (
    <div className="dashboard-container">
      <div className="dashboard-main-grid">
        {/* Left: Small Profile Card */}
        <div className="profile-card card">
          <div className="profile-avatar" style={{ width: 56, height: 56, borderRadius: '50%', background: '#fbcfe8', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 28, fontWeight: 700, color: '#db2777', margin: '0 auto 0.5rem' }}>
            {user?.username?.[0]?.toUpperCase() || 'U'}
          </div>
          <div className="font-bold text-base mb-1 text-center">{user?.username || 'User'}</div>
          <div className="text-xs text-gray-500 mb-1 text-center">Rank {dashboardData?.rank || 'N/A'}</div>
          <div className="badge mb-1 text-center">Level {dashboardData?.level || 1}</div>
          <div className="profile-stats">
            <div><span className="stats-label">Entries</span> <span className="stats-value">{dashboardData?.totalEntries || 0}</span></div>
            <div><span className="stats-label">Points</span> <span className="stats-value">{dashboardData?.totalPoints || 0}</span></div>
            <div><span className="stats-label">Streak</span> <span className="stats-value">{dashboardData?.currentStreak || 0}</span></div>
          </div>
          <div className="flex flex-wrap gap-1 justify-center mt-2">
            {user?.tags?.map(tag => <span key={tag} className="tag">{tag}</span>)}
          </div>
          {/* Badges below profile */}
          <div className="dashboard-badges-card mt-4">
            <div className="card-title">Badges</div>
            <div className="flex gap-2 justify-center items-center mt-2">
              {(dashboardData?.badges || []).map((badge, i) => (
                <img key={i} src={badge.icon} alt={badge.name} style={{ width: 28, height: 28 }} />
              ))}
            </div>
            <div className="text-xs text-center mt-2">Most Recent: {dashboardData?.recentBadge || 'None'}</div>
          </div>
        </div>
        {/* Right: Main Content Grid (no badges card here) */}
        <div className="dashboard-content-grid">
          <div className="card dashboard-mood7-card">
            <div className="card-title">Mood Trend (7 days)</div>
            <ResponsiveContainer width="100%" height={100}>
              <LineChart data={moodTrends7.map(d => ({ date: new Date(d.date).toLocaleDateString(), mood: d.intensity }))}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis domain={[1, 5]} />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="mood" stroke="#ec4899" strokeWidth={2} dot={{ r: 3 }} />
              </LineChart>
            </ResponsiveContainer>
          </div>
          <div className="card dashboard-mood180-card">
            <div className="card-title">Mood Trend (6 months)</div>
            <ResponsiveContainer width="100%" height={100}>
              <LineChart data={moodTrends180.map(d => ({ date: new Date(d.date).toLocaleDateString(), mood: d.intensity }))}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis domain={[1, 5]} />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="mood" stroke="#a259c6" strokeWidth={2} dot={{ r: 3 }} />
              </LineChart>
            </ResponsiveContainer>
          </div>
          <div className="card dashboard-calendar-card">
            <div className="card-title">Calendar Streaks</div>
            <Calendar
              tileContent={({ date, view }) => {
                if (view === 'month') {
                  const dateStr = date.toISOString().split('T')[0];
                  const isStreak = calendarStreaks.some(entry => new Date(entry.createdAt).toISOString().split('T')[0] === dateStr);
                  return isStreak ? <div className="calendar-streak-dot" /> : null;
                }
                return null;
              }}
            />
          </div>
          <div className="card dashboard-entries-card">
            <div className="card-title">Recent Entries</div>
            {recentEntries.length === 0 ? (
              <div className="text-center py-2">
                <div className="text-xl mb-1">üìù</div>
                <p className="text-gray-600 mb-1">No entries yet.</p>
                <button className="btn-primary btn-sm">Write Entry</button>
              </div>
            ) : (
              <div className="space-y-1">
                {recentEntries.map((entry) => (
                  <div key={entry._id} className="entry-preview flex items-center justify-between">
                    <div className="flex-1">
                      <div className="font-semibold text-xs mb-0.5">{entry.title}</div>
                      <div className="text-xs text-gray-500 mb-0.5">{new Date(entry.createdAt).toLocaleDateString()}</div>
                      <div className="text-xs text-gray-700 line-clamp-1">{entry.content}</div>
                    </div>
                    <div className="flex items-center gap-1 ml-2">
                      <span className="text-base">{getMoodEmoji(entry.mood?.primary)}</span>
                      <span className="points-badge">‚≠ê {entry.aiAnalysis?.pointsEarned || 0}</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
          <div className="card dashboard-insights-card">
            <div className="card-title mb-1">AI Insights</div>
            <div className="insights-content text-xs mb-2">
              {dashboardData?.aiInsights || 'Your mood is improving! Keep journaling for more positive trends.'}
            </div>
            <div className="flex gap-2 justify-end">
              <button className="btn-primary btn-sm">New Entry</button>
              <button className="btn-primary btn-sm">Chat with Dr. Luna</button>
              <button className="btn-primary btn-sm">View Insights</button>
            </div>
          </div>
          {dashboardData?.motivationalQuote && (
            <div className="card dashboard-quote-card text-center">
              <div className="text-base mb-1">üí≠</div>
              <blockquote className="text-xs italic text-gray-700 mb-1">
                "{dashboardData.motivationalQuote.text}"
              </blockquote>
              <cite className="text-xs text-gray-500">‚Äî {dashboardData.motivationalQuote.author}</cite>
            </div>
          )}
        </div>
      </div>
      {/* Full-width Activity Heatmap Row (compact, month selector) */}
      <div className="card dashboard-heatmap-row compact-heatmap">
        <div className="flex items-center justify-between mb-2">
          <div className="card-title mb-0">Activity Heatmap</div>
          <div className="heatmap-month-selector">
            <select value={selectedMonth} onChange={e => setSelectedMonth(Number(e.target.value))}>
              {Array.from({ length: 12 }).map((_, i) => (
                <option key={i} value={i}>{new Date(0, i).toLocaleString('default', { month: 'short' })}</option>
              ))}
            </select>
            <select value={selectedYear} onChange={e => setSelectedYear(Number(e.target.value))}>
              {[...new Set(activityData.map(entry => new Date(entry.createdAt).getFullYear()))].sort((a, b) => b - a).map(year => (
                <option key={year} value={year}>{year}</option>
              ))}
            </select>
          </div>
        </div>
        <ActivityHeatmap data={filteredActivityData} small />
      </div>
      {/* Full-width AI Insights Row */}
      <div className="card dashboard-insights-row">
        <div className="card-title mb-1">AI Insights</div>
        <div className="insights-content text-xs mb-2">
          {dashboardData?.aiInsights || 'Your mood is improving! Keep journaling for more positive trends.'}
        </div>
        <div className="flex gap-2 justify-end">
          <button className="btn-primary btn-sm">New Entry</button>
          <button className="btn-primary btn-sm">Chat with Dr. Luna</button>
          <button className="btn-primary btn-sm">View Insights</button>
        </div>
      </div>
      {/* Motivational Quote (if present) */}
      {dashboardData?.motivationalQuote && (
        <div className="card dashboard-quote-card text-center">
          <div className="text-base mb-1">üí≠</div>
          <blockquote className="text-xs italic text-gray-700 mb-1">
            "{dashboardData.motivationalQuote.text}"
          </blockquote>
          <cite className="text-xs text-gray-500">‚Äî {dashboardData.motivationalQuote.author}</cite>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
