import React, { useState } from 'react';
import { Bar, Line, Bubble } from 'react-chartjs-2';
import './App.css'; // Ensure you have the correct path to your CSS file
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend);

const Dashboard = () => {
  const [clicked, setClicked] = useState({});
  const [date, setDate] = useState(new Date('2025-07-05')); // Start with July 5, 2025

  // Generate past 10 days from July 5, 2025
  const getPast10Days = () => {
    const days = [];
    for (let i = 0; i < 10; i++) {
      const d = new Date('2025-07-05');
      d.setDate(d.getDate() - i);
      days.push(d.toLocaleDateString('en-US', { weekday: 'short', day: 'numeric' }));
    }
    return days.reverse(); // [Wed 26, Thu 27, Fri 28, Sat 29, Sun 30, Mon 1, Tue 2, Wed 3, Thu 4, Fri 5]
  };

  const past10Days = getPast10Days();

  // Updated moodData with 7 emotions over past 7 days
  const moodData = {
    labels: past10Days.slice(-7), // Last 7 days for the first graph
    datasets: [
      {
        label: 'Happy',
        data: [20, 18, 22, 15, 25, 19, 23],
        backgroundColor: 'rgba(144, 238, 144, 0.7)', // Light Green
      },
      {
        label: 'Sad',
        data: [5, 7, 4, 6, 3, 8, 5],
        backgroundColor: 'rgba(173, 216, 230, 0.7)', // Light Blue
      },
      {
        label: 'Excited',
        data: [15, 12, 18, 10, 14, 16, 13],
        backgroundColor: 'rgba(255, 182, 193, 0.7)', // Light Pink
      },
      {
        label: 'Calm',
        data: [10, 13, 9, 11, 12, 10, 14],
        backgroundColor: 'rgba(240, 230, 140, 0.7)', // Light Yellow
      },
      {
        label: 'Anxious',
        data: [8, 6, 10, 7, 9, 5, 11],
        backgroundColor: 'rgba(221, 160, 221, 0.7)', // Plum
      },
      {
        label: 'Joyful',
        data: [18, 16, 20, 14, 17, 15, 19],
        backgroundColor: 'rgba(255, 215, 0, 0.7)', // Gold
      },
      {
        label: 'Tired',
        data: [6, 9, 5, 8, 7, 10, 6],
        backgroundColor: 'rgba(211, 211, 211, 0.7)', // Light Gray
      },
    ],
  };

  // Updated bubbleData with daily quality score over past 10 days
  const bubbleData = {
    labels: past10Days,
    datasets: [
      {
        label: 'Day Quality',
        data: past10Days.map((day, index) => {
          // Emotion data for the past 10 days (aligned with moodData and extended)
          const emotions = {
            Happy: [20, 18, 22, 15, 25, 19, 23, 17, 21, 16][index],
            Sad: [5, 7, 4, 6, 3, 8, 5, 9, 6, 4][index],
            Excited: [15, 12, 18, 10, 14, 16, 13, 11, 17, 15][index],
            Calm: [10, 13, 9, 11, 12, 10, 14, 13, 11, 12][index],
            Anxious: [8, 6, 10, 7, 9, 5, 11, 8, 10, 6][index],
            Joyful: [18, 16, 20, 14, 17, 15, 19, 16, 18, 20][index],
            Tired: [6, 9, 5, 8, 7, 10, 6, 9, 5, 8][index],
          };
          // Calculate quality score: +2 for positive (Happy, Excited, Joyful, Calm), -1 for negative (Sad, Anxious, Tired)
          const positiveScore = (emotions.Happy > 0 ? 2 : 0) + (emotions.Excited > 0 ? 2 : 0) +
            (emotions.Joyful > 0 ? 2 : 0) + (emotions.Calm > 0 ? 2 : 0);
          const negativeScore = (emotions.Sad > 0 ? -1 : 0) + (emotions.Anxious > 0 ? -1 : 0) +
            (emotions.Tired > 0 ? -1 : 0);
          const qualityScore = positiveScore + negativeScore;
          // Radius based on the sum of all emotion intensities (variability)
          const totalIntensity = Object.values(emotions).reduce((sum, val) => sum + val, 0);
          return { x: index, y: Math.max(0, Math.min(30, qualityScore + 15)), r: Math.min(15, totalIntensity / 10) };
        }),
        backgroundColor: 'rgba(255, 182, 193, 0.7)', // Light Pink for day quality
      },
    ],
  };

  const lineData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      { label: 'Mood Trend', data: [5, 10, 12, 15, 18, 20], borderColor: 'rgba(255, 182, 193, 0.8)', fill: false },
      { label: 'Energy', data: [3, 7, 10, 14, 18, 25], borderColor: 'rgba(255, 105, 180, 0.8)', fill: false },
      { label: 'Productivity', data: [2, 4, 6, 8, 10, 12], borderColor: 'rgba(255, 160, 180, 0.8)', fill: false },
    ],
  };

  const activityData = {
    labels: ['Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec', 'Jan', 'Feb', 'Mar'],
    datasets: [
      {
        label: 'Activity',
        data: [1, 2, 3, 4, 5, 6, 7, 8, 7, 6, 5, 4],
        backgroundColor: (ctx) => {
          const value = ctx.dataset.data[ctx.dataIndex];
          return value > 6 ? 'rgba(255, 105, 180, 0.7)' : value > 3 ? 'rgba(255, 160, 180, 0.7)' : 'rgba(255, 192, 203, 0.7)';
        },
        borderColor: 'rgba(255, 105, 180, 1)',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: { legend: { position: 'top' }, title: { display: false } },
    animation: { duration: 1000, easing: 'easeInOutQuad' },
    scales: {
      x: { title: { display: true, text: 'Past 10 Days' }, ticks: { callback: (value) => past10Days[value] } },
      y: { beginAtZero: true, max: 30, title: { display: true, text: 'Day Quality Score' } },
    },
  };

  const cardClass = "bg-pink-50 p-6 rounded-3xl shadow hover:shadow-xl transition-all duration-300 w-full";

  // Dummy activity data for calendar
  const activityMap = {
    '2025-07-01': '❤️', '2025-07-02': '😢', '2025-07-03': '❤️', '2025-07-04': '😢', '2025-07-05': '❤️',
    '2025-07-06': '😢', '2025-07-07': '❤️', '2025-07-08': '😢', '2025-07-09': '❤️', '2025-07-10': '😢',
  };

  const handlePrevMonth = () => {
    setDate(new Date(date.setMonth(date.getMonth() - 1)));
  };

  const handleNextMonth = () => {
    setDate(new Date(date.setMonth(date.getMonth() + 1)));
  };

  const tileContent = ({ date, view }) => {
    if (view === 'month') {
      const dateStr = date.toISOString().split('T')[0];
      const activity = activityMap[dateStr];
      if (activity) {
        return <span style={{ color: activity === '❤️' ? '#ff69b4' : '#ff9999', fontSize: '1.2em' }}>{activity}</span>;
      }
    }
    return null;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 via-pink-100 to-pink-200 p-8 grid gap-6 grid-cols-1 md:grid-cols-2">
      {/* First Half: Two Graphs */}
      <div className="flex flex-row">
      <div className={`${cardClass}`} onClick={() => setClicked({ mood: !clicked.mood })}>
        <h2 className="text-xl font-bold text-pink-700 mb-4 font-sacramento text-center">🌸 Mood Past 7 Days</h2>
        <Bar data={moodData} options={chartOptions} />
      </div>

      <div className={`${cardClass}`} onClick={() => setClicked({ bubble: !clicked.bubble })}>
        <h2 className="text-xl font-bold text-pink-600 mb-4 font-sacramento text-center">💖 Your Days So Far</h2>
        <Bubble data={bubbleData} options={chartOptions} />
      </div>
      </div>
      <div className="flex flex-row">
      {/* Second Half: Two Graphs and Marua */}
      <div className={`${cardClass}`} onClick={() => setClicked({ line: !clicked.line })}>
        <h2 className="text-xl font-bold text-pink-700 mb-4 font-sacramento text-center">🌷 Your Mood (6 Months)</h2>
        <Line data={lineData} options={chartOptions} />
      </div>

      <div className={`${cardClass} flex items-center justify-center`}>
        <img
          src="https://via.placeholder.com/80"
          alt="Marua"
          className="rounded-full border-4 border-pink-300 shadow mr-4"
        />
        <div>
          <h2 className="text-xl font-bold text-pink-700 font-sacramento">Marua</h2>
          <div className="bg-pink-400/80 px-3 py-1 rounded-full text-white mt-2 shadow-sm text-center">
            How are you feeling today?
          </div>
        </div>
      </div>

      {/* Activity & Streak with Calendar */}
      <div className={`${cardClass} md:col-span-2`}>
        <h2 className="text-xl font-bold text-pink-700 mb-4 font-sacramento text-center">🌸 Activity & Streak</h2>
        <div className="flex justify-around text-pink-600 text-sm flex-wrap mb-4">
          <p>Total Active Days: <strong>12</strong></p>
          <p>Happy Days: <strong>6</strong></p>
          <p>Sad Days: <strong>6</strong></p>
          <p>Most Common Emotion: <strong>😊 Happy</strong></p>
          <p>Current Streak: <strong>8</strong></p>
          <p>Max Streak: <strong>8</strong></p>
        </div>
        <Bar
          data={activityData}
          options={{
            ...chartOptions,
            animation: {
              duration: clicked.activity ? 0 : 1000,
              easing: 'easeOutCubic',
            },
          }}
        />
        <p className="text-pink-500 text-xs mt-2 text-center cursor-pointer">🌸 Learn how we count contributions</p>
</div>
        {/* Calendar with react-calendar */}
        <div className="mt-6 bg-pink-100 p-4 rounded-2xl shadow">
          <div className="flex justify-between items-center mb-4">
            <button
              onClick={handlePrevMonth}
              className="bg-pink-300 text-white px-3 py-1 rounded-full hover:bg-pink-400"
            >
              👈
            </button>
            <h3 className="text-2xl font-sacramento text-pink-700">
              {date.toLocaleString('default', { month: 'long' })} {date.getFullYear()}
            </h3>
            <button
              onClick={handleNextMonth}
              className="bg-pink-300 text-white px-3 py-1 rounded-full hover:bg-pink-400"
            >
              👉
            </button>
          </div>
          <Calendar
            onChange={setDate}
            value={date}
            tileContent={tileContent}
            next2Label={null}
            prev2Label={null}
            showNeighboringMonth={false}
            className="react-calendar"
          />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
