import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './Insights.css';

const Insights = ({ user }) => {
  const { username } = useParams();
  const [insights, setInsights] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [timeRange, setTimeRange] = useState('30'); // days

  const API_BASE_URL = 'http://localhost:5001/api';

  // Fetch insights
  const fetchInsights = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_BASE_URL}/insights/comprehensive?days=${timeRange}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setInsights(data.data);
      } else {
        setError('Failed to fetch insights');
      }
    } catch (error) {
      setError('Network error');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchInsights();
  }, [timeRange]);

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 to-green-50 flex items-center justify-center">
        <div className="spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 to-green-50 p-6">
        <div className="max-w-6xl mx-auto">
          <div className="error-message">
            <h2 className="text-xl font-semibold mb-2">‚ùå Error Loading Insights</h2>
            <p>{error}</p>
            <button onClick={fetchInsights} className="btn-primary mt-4">
              üîÑ Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (!insights) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 to-green-50 p-6">
        <div className="max-w-6xl mx-auto">
          <div className="empty-state">
            <div className="empty-state-icon">üìä</div>
            <h2 className="text-xl font-semibold mb-2">No Insights Available</h2>
            <p className="text-pink-600 mb-4">Start journaling to see your insights!</p>
          </div>
        </div>
      </div>
    );
  }

  const moodColors = {
    happy: '#fbbf24',
    sad: '#60a5fa',
    excited: '#f87171',
    calm: '#34d399',
    anxious: '#a78bfa',
    joyful: '#fbbf24',
    tired: '#9ca3af',
    neutral: '#9ca3af'
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 to-green-50 p-6">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold gradient-text mb-4" style={{ fontFamily: "'Handlee', cursive" }}>
            üìä Your Insights & Analytics
          </h1>
          <p className="text-pink-600 text-lg mb-4">
            Discover patterns in your emotional journey, {user?.username}! üå∏
          </p>
          
          {/* Time Range Selector */}
          <div className="flex justify-center gap-4">
            <select
              value={timeRange}
              onChange={(e) => setTimeRange(e.target.value)}
              className="input-field w-auto"
            >
              <option value="7">Last 7 days</option>
              <option value="30">Last 30 days</option>
              <option value="90">Last 3 months</option>
              <option value="365">Last year</option>
            </select>
          </div>
        </div>

        {/* Overview Stats */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="card text-center">
            <div className="text-3xl mb-2">üìù</div>
            <h3 className="text-lg font-semibold mb-1">Total Entries</h3>
            <p className="text-2xl font-bold gradient-text">{insights.totalEntries}</p>
          </div>
          
          <div className="card text-center">
            <div className="text-3xl mb-2">‚≠ê</div>
            <h3 className="text-lg font-semibold mb-1">Total Points</h3>
            <p className="text-2xl font-bold gradient-text">{insights.totalPoints}</p>
          </div>
          
          <div className="card text-center">
            <div className="text-3xl mb-2">üî•</div>
            <h3 className="text-lg font-semibold mb-1">Current Streak</h3>
            <p className="text-2xl font-bold gradient-text">{insights.currentStreak} days</p>
          </div>
          
          <div className="card text-center">
            <div className="text-3xl mb-2">üìà</div>
            <h3 className="text-lg font-semibold mb-1">Average Points</h3>
            <p className="text-2xl font-bold gradient-text">{insights.averagePoints?.toFixed(1) || 0}</p>
          </div>
        </div>

        {/* Mood Analysis */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          <div className="card">
            <h2 className="text-2xl font-bold mb-4 gradient-text">üòä Mood Distribution</h2>
            <div className="space-y-3">
              {insights.moodStats && Object.entries(insights.moodStats).map(([mood, count]) => (
                <div key={mood} className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <span className="text-lg">{getMoodEmoji(mood)}</span>
                    <span className="capitalize">{mood}</span>
                  </div>
                  <div className="flex items-center gap-2">
                    <div className="w-24 h-2 bg-gray-200 rounded-full overflow-hidden">
                      <div 
                        className="h-full rounded-full transition-all duration-500"
                        style={{ 
                          width: `${(count / insights.totalEntries) * 100}%`,
                          backgroundColor: moodColors[mood] || '#9ca3af'
                        }}
                      ></div>
                    </div>
                    <span className="text-sm font-medium w-8 text-right">{count}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="card">
            <h2 className="text-2xl font-bold mb-4 gradient-text">üìÖ Writing Patterns</h2>
            <div className="space-y-4">
              <div className="flex justify-between items-center">
                <span>Most Active Day:</span>
                <span className="font-semibold">{insights.mostActiveDay || 'N/A'}</span>
              </div>
              <div className="flex justify-between items-center">
                <span>Average Words per Entry:</span>
                <span className="font-semibold">{insights.averageWordsPerEntry || 0}</span>
              </div>
              <div className="flex justify-between items-center">
                <span>Completion Rate:</span>
                <span className="font-semibold">{insights.completionRate || 0}%</span>
              </div>
              <div className="flex justify-between items-center">
                <span>Best Day Score:</span>
                <span className="font-semibold">{insights.bestDayScore || 0} points</span>
              </div>
            </div>
          </div>
        </div>

        {/* AI Insights */}
        {insights.aiInsights && (
          <div className="card mb-8">
            <h2 className="text-2xl font-bold mb-4 gradient-text">ü§ñ Dr. Luna's Analysis</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <h3 className="text-lg font-semibold mb-3 text-pink-600">Emotional Patterns</h3>
                <p className="text-gray-700 leading-relaxed">{insights.aiInsights.emotionalPatterns}</p>
              </div>
              <div>
                <h3 className="text-lg font-semibold mb-3 text-green-600">Growth Areas</h3>
                <p className="text-gray-700 leading-relaxed">{insights.aiInsights.growthAreas}</p>
              </div>
            </div>
            
            {insights.aiInsights.recommendations && (
              <div className="mt-6">
                <h3 className="text-lg font-semibold mb-3 gradient-text">Personalized Recommendations</h3>
                <ul className="space-y-2">
                  {insights.aiInsights.recommendations.map((rec, index) => (
                    <li key={index} className="flex items-start gap-2">
                      <span className="text-pink-500 mt-1">‚Ä¢</span>
                      <span className="text-gray-700">{rec}</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}

        {/* Progress Charts */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          <div className="card">
            <h2 className="text-2xl font-bold mb-4 gradient-text">üìà Points Trend</h2>
            <div className="h-64 flex items-end justify-center gap-2">
              {insights.pointsTrend && insights.pointsTrend.map((point, index) => (
                <div key={index} className="flex flex-col items-center">
                  <div 
                    className="w-8 bg-gradient-to-t from-pink-400 to-green-400 rounded-t transition-all duration-500"
                    style={{ height: `${(point / 50) * 200}px` }}
                  ></div>
                  <span className="text-xs mt-1 text-gray-600">{point}</span>
                </div>
              ))}
            </div>
          </div>

          <div className="card">
            <h2 className="text-2xl font-bold mb-4 gradient-text">üéØ Goals & Achievements</h2>
            <div className="space-y-4">
              {insights.achievements && insights.achievements.map((achievement, index) => (
                <div key={index} className="flex items-center gap-3 p-3 bg-pink-50 rounded-lg">
                  <span className="text-2xl">üèÜ</span>
                  <div>
                    <h4 className="font-semibold">{achievement.title}</h4>
                    <p className="text-sm text-gray-600">{achievement.description}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Weekly Summary */}
        {insights.weeklySummary && (
          <div className="card">
            <h2 className="text-2xl font-bold mb-4 gradient-text">üìã Weekly Summary</h2>
            <div className="prose max-w-none">
              <p className="text-gray-700 leading-relaxed">{insights.weeklySummary}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// Helper function to get mood emoji
const getMoodEmoji = (mood) => {
  const emojis = {
    happy: 'üòä',
    sad: 'üò¢',
    excited: 'ü§©',
    calm: 'üòå',
    anxious: 'üò∞',
    joyful: 'üòÑ',
    tired: 'üò¥',
    neutral: 'üòê'
  };
  return emojis[mood] || 'üòê';
};

export default Insights;
