import { motion } from "framer-motion";
import { useState, useEffect } from "react";

const features = [
  "ðŸ§  AI-powered Emotion Insights",
  "ðŸ“ˆ Mood Trend Visualization",
  "âœï¸ Daily Journaling",
  "ðŸ” Private & Secure",
  "ðŸŽ¨ Calming UI & Experience",
  "ðŸ’¬ Emotional Support from SereneAI",
];

const Home = () => {
  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentPage((prev) => (prev + 1) % features.length);
    }, 4000); // flips every 4s
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="flex flex-col items-center justify-center min-h-[calc(100vh-5rem)] bg-pink-50 font-figtree py-4">
      <div className="relative w-full max-w-7xl h-[calc(100vh-6rem)] flex shadow-2xl rounded-3xl overflow-hidden">
        {/* Spiral */}
        <div className="absolute left-[-30px] top-0 h-full flex flex-col justify-around z-20">
          {[...Array(10)].map((_, i) => (
            <div
              key={i}
              className="w-3 h-3 rounded-full bg-gray-400 border border-gray-500"
            />
          ))}
        </div>

        {/* Left Page */}
        <div className="relative flex-1 bg-white bg-opacity-90 border-r border-pink-200">
          <div className="absolute inset-0 bg-[linear-gradient(to_bottom,transparent_95%,#e5e7eb_95%)] bg-[size:100%_2.5rem]" />
          <div className="p-10 flex flex-col justify-center h-full">
            <h1 className="text-5xl font-sacramento text-pink-500 mb-6 text-center">
              MoodJournal AI
            </h1>
            <p className="text-lg text-gray-600 text-center">
              Emotional clarity & calm reflection.
            </p>
            <div className="mt-10 flex flex-wrap gap-4 justify-center">
              <img
                src="/polaroid1.jpg"
                alt="polaroid"
                className="w-40 h-40 object-cover shadow-md rotate-[-5deg]"
              />
              <img
                src="/polaroid2.jpg"
                alt="polaroid"
                className="w-40 h-40 object-cover shadow-md rotate-2"
              />
              <img
                src="/polaroid3.jpg"
                alt="polaroid"
                className="w-40 h-40 object-cover shadow-md rotate-[-3deg]"
              />
            </div>
          </div>
        </div>

        {/* Right Page */}
        <div className="relative flex-1 bg-white bg-opacity-90">
          <div className="absolute inset-0 bg-[linear-gradient(to_bottom,transparent_95%,#e5e7eb_95%)] bg-[size:100%_2.5rem]" />

          {/* Auto-flipping page */}
          <motion.div
            key={currentPage}
            initial={{ rotateY: 0 }}
            animate={{ rotateY: 180 }}
            transition={{ duration: 1.2, ease: "easeInOut" }}
            className="absolute inset-0 bg-white bg-opacity-90 border-l border-pink-200 z-10 origin-left"
            style={{ transformStyle: "preserve-3d" }}
          >
            <div className="absolute inset-0 p-10 flex flex-col justify-center items-center">
              <h2 className="text-2xl text-indigo-600 font-semibold text-center">
                {features[currentPage]}
              </h2>
            </div>

            {/* Back of page */}
            <div
              className="absolute inset-0 bg-white bg-opacity-90 border-l border-pink-200"
              style={{
                transform: "rotateY(180deg)",
                backfaceVisibility: "hidden",
              }}
            >
              <div className="p-10 flex flex-col justify-center items-center">
                <p className="text-xl text-gray-400">Next pageâ€¦</p>
              </div>
            </div>
          </motion.div>

          {/* Static back page */}
          <div className="absolute inset-0 p-10 flex flex-col justify-center items-center z-0">
            <h2 className="text-xl text-indigo-400">Waitingâ€¦</h2>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
