import { useState } from 'react';
import './App.css';
import './output.css';
import { Link } from 'react-router-dom';

const Login = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const result = await onLogin(formData.email, formData.password);
      
      if (result.success) {
        setMessage(result.message);
        // Success message will be shown briefly before redirect
      } else {
        setMessage(result.message);
      }
    } catch (error) {
      setMessage('An error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-4" style={{backgroundImage: `url('bg.png')`, backgroundSize: 'cover'}}>
      <div className="bg-pink-100 rounded-2xl shadow-xl p-8 w-full max-w-md" style={{marginTop: '-90px'}}>
        <h1
          className="text-4xl text-pink-600 text-center mb-6"
          style={{ fontFamily: "'Handlee', cursive" }}
        >
          ðŸŒ¸ Welcome Back!
        </h1>
        
        {message && (
          <div className={`mb-4 p-3 rounded-lg text-center ${
            message.includes('Welcome') || message.includes('successfully') 
              ? 'bg-green-100 text-green-700' 
              : 'bg-red-100 text-red-700'
          }`}>
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
            className="w-full p-4 rounded-xl border border-pink-200 focus:outline-none focus:ring-2 focus:ring-green-300 text-lg"
            style={{ fontFamily: "'Nunito', sans-serif" }}
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
            className="w-full p-4 rounded-xl border border-pink-200 focus:outline-none focus:ring-2 focus:ring-pink-300 text-lg"
            style={{ fontFamily: "'Nunito', sans-serif" }}
          />
          <button
            type="submit"
            disabled={loading}
            className={`w-full bg-gradient-to-r from-pink-300 to-green-300 text-pink-900 font-bold py-3 rounded-full shadow-md hover:scale-105 transform transition-all duration-300 ease-in-out text-xl ${
              loading ? 'opacity-50 cursor-not-allowed' : ''
            }`}
            style={{ fontFamily: "'Handlee', cursive", marginTop: '30px' }}
          >
            {loading ? 'ðŸŒ¸ Loading...' : 'ðŸŒˆ Login âœ¨'}
          </button>
        </form>
        
        <div className="login-signup-link" style={{textAlign:'center', marginTop:'1.5rem'}}>
          <span>Don't have an account? </span>
          <Link to="/signin" className="signup-link">Sign Up</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
