import React, { useState } from 'react';
import './Contact.css';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    
    // Simulate form submission
    setTimeout(() => {
      setIsSubmitting(false);
      setSubmitStatus('success');
      setFormData({ name: '', email: '', subject: '', message: '' });
      
      // Reset status after 3 seconds
      setTimeout(() => setSubmitStatus(null), 3000);
    }, 2000);
  };

  const contactInfo = [
    {
      icon: 'üìß',
      title: 'Email',
      value: 'hello@safespace.com',
      description: 'Send us an email anytime'
    },
    {
      icon: 'üí¨',
      title: 'Live Chat',
      value: 'Available 24/7',
      description: 'Chat with our support team'
    },
    {
      icon: 'üì±',
      title: 'Social Media',
      value: '@safespace',
      description: 'Follow us for updates'
    },
    {
      icon: 'üìç',
      title: 'Office',
      value: 'Digital Nomad',
      description: 'We work remotely worldwide'
    }
  ];

  const faqs = [
    {
      question: "How do I get started with journaling?",
      answer: "Simply create an account and start writing your first entry. Our AI assistant Dr. Luna will help guide you through the process and provide insights."
    },
    {
      question: "Is my data secure and private?",
      answer: "Absolutely! We use industry-standard encryption and your journal entries are completely private. Only you can access your personal content."
    },
    {
      question: "How does the AI analysis work?",
      answer: "Our AI analyzes your entries for emotional patterns, provides therapeutic insights, and helps you track your mental health journey over time."
    },
    {
      question: "Can I export my journal entries?",
      answer: "Yes! You can export your entries in various formats including PDF and text files for your personal records."
    },
    {
      question: "What if I forget my password?",
      answer: "Use the 'Forgot Password' feature on the login page. We'll send you a secure reset link to your registered email."
    }
  ];

  return (
    <div className="contact-container">
      <div className="contact-header">
        <div className="header-content">
          <h1 className="contact-title font-handwriting">
            <span className="title-icon">üìû</span>
            Get in Touch
          </h1>
          <p className="contact-subtitle">
            We'd love to hear from you! Reach out to us for support, feedback, or just to say hello.
          </p>
        </div>
        <div className="header-decoration">
          <div className="floating-icon">üíå</div>
          <div className="floating-icon">üì¨</div>
          <div className="floating-icon">üìß</div>
        </div>
      </div>

      <div className="contact-content">
        <div className="contact-info-section">
          <div className="info-card">
            <h3 className="info-title">Contact Information</h3>
            <div className="info-list">
              {contactInfo.map((info, index) => (
                <div key={index} className="info-item">
                  <div className="info-icon">{info.icon}</div>
                  <div className="info-content">
                    <h4 className="info-name">{info.title}</h4>
                    <p className="info-value">{info.value}</p>
                    <p className="info-description">{info.description}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="faq-card">
            <h3 className="faq-title">Frequently Asked Questions</h3>
            <div className="faq-list">
              {faqs.map((faq, index) => (
                <div key={index} className="faq-item">
                  <h4 className="faq-question">{faq.question}</h4>
                  <p className="faq-answer">{faq.answer}</p>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="contact-form-section">
          <div className="form-card">
            <h3 className="form-title">Send us a Message</h3>
            <p className="form-subtitle">
              Fill out the form below and we'll get back to you as soon as possible.
            </p>

            {submitStatus === 'success' && (
              <div className="success-message">
                <span className="success-icon">‚úÖ</span>
                <div className="success-content">
                  <h4>Message Sent!</h4>
                  <p>Thank you for reaching out. We'll get back to you soon!</p>
                </div>
              </div>
            )}

            <form onSubmit={handleSubmit} className="contact-form">
              <div className="form-group">
                <label htmlFor="name" className="form-label">Full Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className="form-input"
                  placeholder="Enter your full name"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="email" className="form-label">Email Address</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className="form-input"
                  placeholder="Enter your email address"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="subject" className="form-label">Subject</label>
                <select
                  id="subject"
                  name="subject"
                  value={formData.subject}
                  onChange={handleInputChange}
                  className="form-select"
                  required
                >
                  <option value="">Select a subject</option>
                  <option value="general">General Inquiry</option>
                  <option value="support">Technical Support</option>
                  <option value="feedback">Feedback & Suggestions</option>
                  <option value="bug">Bug Report</option>
                  <option value="feature">Feature Request</option>
                  <option value="partnership">Partnership</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="message" className="form-label">Message</label>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleInputChange}
                  className="form-textarea"
                  rows={6}
                  placeholder="Tell us how we can help you..."
                  required
                ></textarea>
              </div>

              <button 
                type="submit" 
                className={`submit-btn ${isSubmitting ? 'submitting' : ''}`}
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  <>
                    <div className="spinner"></div>
                    <span>Sending...</span>
                  </>
                ) : (
                  <>
                    <span className="btn-icon">üì§</span>
                    <span>Send Message</span>
                  </>
                )}
              </button>
            </form>
          </div>
        </div>
      </div>

      <div className="contact-footer">
        <div className="footer-content">
          <div className="footer-section">
            <h4 className="footer-title">Support Hours</h4>
            <p className="footer-text">Monday - Friday: 9 AM - 6 PM EST</p>
            <p className="footer-text">Weekend: 10 AM - 4 PM EST</p>
          </div>
          <div className="footer-section">
            <h4 className="footer-title">Response Time</h4>
            <p className="footer-text">We typically respond within 24 hours</p>
            <p className="footer-text">Urgent issues: Within 4 hours</p>
          </div>
          <div className="footer-section">
            <h4 className="footer-title">Follow Us</h4>
            <div className="social-links">
              <a href="#" className="social-link">üìò Facebook</a>
              <a href="#" className="social-link">üê¶ Twitter</a>
              <a href="#" className="social-link">üì∑ Instagram</a>
              <a href="#" className="social-link">üíº LinkedIn</a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
