import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

const stats = [
  { label: "Journals Logged", value: "12,345+" },
  { label: "Emotions Tracked", value: "8,910+" },
  { label: "Happy Users", value: "4,567+" },
];

const WebsiteStats = () => {
  const navigate = useNavigate();

  const handleStart = () => {
    // optional: delay & animate before navigating
    navigate("/next");
  };

  return (
    <motion.div
      initial={{ rotateY: -90, opacity: 0 }}
      animate={{ rotateY: 0, opacity: 1 }}
      exit={{ rotateY: 90, opacity: 0 }}
      transition={{ duration: 0.8, ease: "easeInOut" }}
      style={{ perspective: 1000 }}
      className="min-h-screen flex flex-col justify-center items-center bg-gradient-to-br from-pink-50 to-green-50 font-figtree"
    >
      <div className="bg-white/80 rounded-2xl shadow-xl p-8 max-w-xl w-full text-center relative">
        <h2 className="text-3xl font-sacramento text-pink-500 mb-4">
          Why MoodJournal AI?
        </h2>
        <p className="text-gray-600 mb-6 px-4">
          Your personal space to reflect, grow, and track your emotional
          journey â€” beautifully and privately.
        </p>

        <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-6">
          {stats.map((stat, idx) => (
            <motion.div
              key={idx}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: idx * 0.3 }}
              className="bg-pink-100/50 rounded-lg p-4 shadow"
            >
              <div className="text-xl text-indigo-700 font-bold">
                {stat.value}
              </div>
              <div className="text-sm text-gray-700">{stat.label}</div>
            </motion.div>
          ))}
        </div>

        <motion.button
          onClick={handleStart}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="px-6 py-2 bg-pink-400 text-white rounded-full shadow hover:bg-pink-500 transition"
        >
          Get Started Now
        </motion.button>
      </div>
    </motion.div>
  );
};

export default WebsiteStats;
