import React, { useState } from 'react';
import Sidebar from './Sidebar';
import { Bar, Line, Bubble } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend);

const Dashboard = () => {
  const [clicked, setClicked] = useState({ mood: false, bubble: false, line: false, activity: false });

  const moodData = {
    labels: ['Item 1', 'Item 2', 'Item 3', 'Item 4', 'Item 5', 'Item 6', 'Item 7'],
    datasets: [
      { label: 'Item 1', data: [5], backgroundColor: 'rgba(135, 206, 235, 0.6)' },
      { label: 'Item 2', data: [10], backgroundColor: 'rgba(135, 206, 235, 0.6)' },
      { label: 'Item 3', data: [15], backgroundColor: 'rgba(135, 206, 235, 0.6)' },
      { label: 'Item 4', data: [20], backgroundColor: 'rgba(75, 192, 192, 0.6)' },
      { label: 'Item 5', data: [25], backgroundColor: 'rgba(75, 192, 192, 0.6)' },
      { label: 'Item 6', data: [30], backgroundColor: 'rgba(153, 102, 255, 0.6)' },
      { label: 'Item 7', data: [15], backgroundColor: 'rgba(255, 99, 132, 0.6)' },
    ],
  };

  const lineData = {
    labels: ['Jan', 'Feb', 'March', 'April', 'May', 'June'],
    datasets: [
      { label: 'Plans', data: [5, 10, 12, 15, 18, 20], borderColor: 'rgba(255, 105, 180, 0.8)', fill: false },
      { label: 'Bundles', data: [3, 7, 10, 14, 18, 25], borderColor: 'rgba(255, 20, 147, 0.8)', fill: false },
      { label: 'Products', data: [2, 4, 6, 8, 10, 12], borderColor: 'rgba(255, 182, 193, 0.8)', fill: false },
    ],
  };

  const bubbleData = {
    datasets: [
      {
        label: 'Your days so far',
        data: [
          { x: 10, y: 20, r: 15, label: 'North America' },
          { x: 20, y: 30, r: 20, label: 'Europe' },
          { x: 30, y: 40, r: 25, label: 'Asia' },
          { x: 40, y: 50, r: 20, label: 'South America' },
          { x: 50, y: 60, r: 15, label: 'Australia' },
          { x: 60, y: 70, r: 10, label: 'Subgroup 1' },
          { x: 70, y: 80, r: 15, label: 'Subgroup 2' },
        ],
        backgroundColor: 'rgba(255, 105, 180, 0.6)',
      },
    ],
  };

  const activityData = {
    labels: ['Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec', 'Jan', 'Feb', 'Mar'],
    datasets: [
      {
        label: 'Activity',
        data: [1, 2, 3, 4, 5, 6, 7, 8, 7, 6, 5, 4],
        backgroundColor: (context) => {
          const value = context.dataset.data[context.dataIndex];
          return value > 6 ? 'rgba(0, 128, 0, 0.7)' : value > 3 ? 'rgba(144, 238, 144, 0.7)' : 'rgba(211, 211, 211, 0.7)';
        },
        borderColor: 'rgba(255, 105, 180, 1)',
        borderWidth: 1,
      },
    ],
  };

  const handleClick = (section) => {
    setClicked((prev) => ({ ...prev, [section]: !prev[section] }));
  };

  const chartOptions = {
    responsive: true,
    plugins: { legend: { position: 'top' }, title: { display: false } },
    onClick: () => handleClick('activity'),
    animation: {
      duration: 1000,
      easing: 'easeInOutQuad',
    },
    scales: {
      y: { beginAtZero: true },
    },
  };

  return (
    <div className="min-h-screen bg-pink-100 p-6 flex flex-wrap gap-6 justify-center">
      <Sidebar />
      <div
  className="bg-pink-200 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300 w-72"  
  onClick={() => handleClick('mood')}
>
        <h2 className="text-2xl font-sacramento text-pink-700">Your mood past 7 days</h2>
        <Bar data={moodData} options={chartOptions} />
      </div>
      <div className="bg-pink-200 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300" onClick={() => handleClick('bubble')}>
        <h2 className="text-2xl font-sacramento text-pink-700">Your days so far</h2>
        <Bubble data={bubbleData} options={chartOptions} />
      </div>
      <div className="bg-pink-300 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300 flex items-center">
        <img src="https://via.placeholder.com/100" alt="Marua" className="rounded-full mr-4" />
        <div>
          <h2 className="text-2xl font-sacramento text-pink-700">Marua</h2>
          <div className="bg-pink-400 p-2 rounded text-white">How are you feeling today?</div>
        </div>
      </div>
      <div className="bg-pink-200 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300" onClick={() => handleClick('line')}>
        <h2 className="text-2xl font-sacramento text-pink-700">Bundles drive revenue growth during last two months of Q2</h2>
        <Line data={lineData} options={chartOptions} />
      </div>
      <div className="bg-pink-200 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300" onClick={() => handleClick('activity')}>
        <h2 className="text-2xl font-sacramento text-pink-700">Total active days: 12</h2>
        <p>Total happy days: 6</p>
        <p>Your sad days: 6</p>
        <p>Your most common emotion: happy</p>
        <p>Current streak: 8</p>
        <p>MAX streak: 8</p>
        <Bar
          data={activityData}
          options={{
            ...chartOptions,
            onClick: () => {
              handleClick('activity');
              chartOptions.animation.duration = clicked.activity ? 0 : 1000; // Toggle animation
            },
            animation: {
              duration: clicked.activity ? 0 : 1000,
              easing: 'easeInOutQuad',
              onComplete: () => {
                if (clicked.activity) {
                  chartOptions.animation.scale = 1.1;
                } else {
                  chartOptions.animation.scale = 1;
                }
              },
            },
          }}
        />
        <p className="text-blue-500 cursor-pointer">Learn how we count contributions.</p>
      </div>
    </div>
  );
};

export default Dashboard;
