import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = ({ user }) => {
  const { username } = useParams();
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [recentEntries, setRecentEntries] = useState([]);

  const API_BASE_URL = 'http://localhost:5001/api';

  // Fetch dashboard data
  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      
      // Fetch dashboard stats
      const statsResponse = await fetch(`${API_BASE_URL}/user/dashboard`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Fetch recent entries
      const entriesResponse = await fetch(`${API_BASE_URL}/journal?limit=5&sortBy=date&sortOrder=desc`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (statsResponse.ok && entriesResponse.ok) {
        const statsData = await statsResponse.json();
        const entriesData = await entriesResponse.json();
        
        setDashboardData(statsData.data);
        setRecentEntries(entriesData.data.entries);
      } else {
        setError('Failed to fetch dashboard data');
      }
    } catch (error) {
      setError('Network error');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 to-green-50 flex items-center justify-center">
        <div className="spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 to-green-50 p-6">
        <div className="max-w-6xl mx-auto">
          <div className="error-message">
            <h2 className="text-xl font-semibold mb-2">‚ùå Error Loading Dashboard</h2>
            <p>{error}</p>
            <button onClick={fetchDashboardData} className="btn-primary mt-4">
              üîÑ Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  const getLevelInfo = (level) => {
    const levels = {
      1: { name: 'Novice Journaler', color: '#6b7280', icon: 'üå±' },
      2: { name: 'Mindful Writer', color: '#10b981', icon: 'üìù' },
      3: { name: 'Emotional Explorer', color: '#3b82f6', icon: 'üîç' },
      4: { name: 'Wellness Warrior', color: '#8b5cf6', icon: '‚öîÔ∏è' },
      5: { name: 'Sage of Self', color: '#f59e0b', icon: 'üßò' },
      6: { name: 'Master of Mindfulness', color: '#ec4899', icon: 'üåü' }
    };
    return levels[level] || levels[1];
  };

  const getMoodEmoji = (mood) => {
    const emojis = {
      happy: 'üòä',
      sad: 'üò¢',
      excited: 'ü§©',
      calm: 'üòå',
      anxious: 'üò∞',
      joyful: 'üòÑ',
      tired: 'üò¥',
      neutral: 'üòê'
    };
    return emojis[mood] || 'üòê';
  };

  const levelInfo = getLevelInfo(dashboardData?.level || 1);

  return (
    <div className="dashboard-horizontal-container">
      <div className="dashboard-horizontal-row">
        {/* Stats Cards */}
        <div className="dashboard-horizontal-section stats-section">
          <div className="stats-row">
            <div className="stats-card">
              <div className="stats-icon">üìù</div>
              <h3 className="text-lg font-semibold mb-1">Total Entries</h3>
              <p className="stats-number">{dashboardData?.totalEntries || 0}</p>
            </div>
            
            <div className="stats-card">
              <div className="stats-icon">‚≠ê</div>
              <h3 className="text-lg font-semibold mb-1">Total Points</h3>
              <p className="stats-number">{dashboardData?.totalPoints || 0}</p>
            </div>
            
            <div className="stats-card">
              <div className="stats-icon">üî•</div>
              <h3 className="text-lg font-semibold mb-1">Current Streak</h3>
              <p className="stats-number">{dashboardData?.currentStreak || 0} days</p>
            </div>
            
            <div className="stats-card">
              <div className="stats-icon">üìà</div>
              <h3 className="text-lg font-semibold mb-1">Average Points</h3>
              <p className="stats-number">{dashboardData?.averagePoints?.toFixed(1) || 0}</p>
            </div>
          </div>
        </div>
        {/* Level & Progress */}
        <div className="dashboard-horizontal-section level-section">
          <div className="card">
            <h2 className="text-2xl font-bold mb-4 gradient-text">üéØ Your Level & Progress</h2>
            <div className="text-center p-6">
              <div className="text-6xl mb-4">{levelInfo.icon}</div>
              <h3 className="text-xl font-semibold mb-2" style={{ color: levelInfo.color }}>
                {levelInfo.name}
              </h3>
              <p className="text-gray-600 mb-4">Level {dashboardData?.level || 1}</p>
              
              {/* Progress Bar */}
              <div className="w-full bg-gray-200 rounded-full h-4 mb-4">
                <div 
                  className="h-4 rounded-full transition-all duration-500"
                  style={{ 
                    width: `${((dashboardData?.totalPoints || 0) % 1000) / 10}%`,
                    background: `linear-gradient(90deg, ${levelInfo.color}, #ec4899)`
                  }}
                ></div>
              </div>
              
              <p className="text-sm text-gray-600">
                {dashboardData?.totalPoints || 0} / {Math.ceil((dashboardData?.totalPoints || 0) / 1000) * 1000} points to next level
              </p>
            </div>
          </div>
        </div>
        {/* Recent Entries */}
        <div className="dashboard-horizontal-section entries-section">
          <div className="card mb-8">
            <h2 className="text-2xl font-bold mb-4 gradient-text">üìù Recent Entries</h2>
            {recentEntries.length === 0 ? (
              <div className="text-center py-8">
                <div className="text-4xl mb-4">üìù</div>
                <p className="text-gray-600 mb-4">No entries yet. Start your journaling journey!</p>
                <button className="btn-primary">‚ú® Write Your First Entry</button>
              </div>
            ) : (
              <div className="space-y-4">
                {recentEntries.map((entry) => (
                  <div key={entry._id} className="entry-preview">
                    <div className="flex justify-between items-start">
                      <div className="flex-1">
                        <h3 className="font-semibold text-lg mb-1">{entry.title}</h3>
                        <p className="text-gray-600 text-sm mb-2">
                          {new Date(entry.createdAt).toLocaleDateString()}
                        </p>
                        <p className="text-gray-700 line-clamp-2">{entry.content}</p>
                      </div>
                      <div className="flex items-center gap-2 ml-4">
                        <span className="text-lg">{getMoodEmoji(entry.mood?.primary)}</span>
                        <span className="points-badge">‚≠ê {entry.aiAnalysis?.pointsEarned || 0}</span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
      {/* Quick Actions - now in a horizontal row */}
      <div className="dashboard-actions-row">
        <button className="btn-primary action-btn">‚ú® New Entry</button>
        <button className="btn-primary action-btn">ü§ñ Chat with Dr. Luna</button>
        <button className="btn-primary action-btn">üìä View Insights</button>
      </div>
      {/* Motivational Quote */}
      {dashboardData?.motivationalQuote && (
        <div className="card mt-8 text-center">
          <div className="text-3xl mb-4">üí≠</div>
          <blockquote className="text-lg italic text-gray-700 mb-2">
            "{dashboardData.motivationalQuote.text}"
          </blockquote>
          <cite className="text-sm text-gray-600">‚Äî {dashboardData.motivationalQuote.author}</cite>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
