import { motion, useAnimation } from "framer-motion";
import { useState, useEffect } from "react";

const features = [
  "ðŸ§  AI-powered Emotion Insights",
  "ðŸ“ˆ Mood Trend Visualization",
  "âœï¸ Daily Journaling",
  "ðŸ” Private & Secure",
  "ðŸŽ¨ Calming UI & Experience",
  "ðŸ’¬ Emotional Support from SereneAI",
];

const Home = () => {
  const [currentPage, setCurrentPage] = useState(0);
  const controls = useAnimation();

  // Page flip sound
  const playPageFlipSound = () => {
    const audio = new Audio('https://www.soundjay.com/sounds/paper-flip-1.mp3');
    audio.play().catch((err) => console.log('Audio playback failed:', err));
  };

  // Auto-flip pages every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      playPageFlipSound();
      setCurrentPage((prev) => (prev + 1) % features.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // Page flip animation
  useEffect(() => {
    controls.start({
      rotateY: [0, 180],
      transition: { duration: 0.8, ease: 'easeInOut' },
    });
  }, [currentPage, controls]);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-pink-50 font-figtree py-10">
      <div className="relative w-full max-w-4xl h-[600px] perspective-1000">
        {/* Spiral binding */}
        <div className="absolute left-[-20px] top-0 h-full flex flex-col justify-around z-20">
          {[...Array(10)].map((_, i) => (
            <div key={i} className="w-4 h-4 rounded-full bg-gray-400 border border-gray-500"></div>
          ))}
        </div>

        {/* Notebook page */}
        <motion.div
          className="relative w-full h-full rounded-3xl shadow-2xl border border-pink-200 bg-white bg-opacity-90 p-10 overflow-hidden"
          animate={controls}
          style={{ transformStyle: 'preserve-3d' }}
        >
          {/* Red margin line */}
          <div className="absolute left-[40px] top-0 h-full w-[2px] bg-red-500 z-10"></div>

          {/* Notebook lines */}
          <div className="absolute inset-0 bg-[linear-gradient(to_bottom,transparent_95%,#e5e7eb_95%)] bg-[length:100%_2.5rem] z-0 pointer-events-none"></div>

          {/* Content */}
          <div className="relative z-10">
            <h1 className="text-5xl font-sacramento text-pink-600 mb-6 text-center">
              MoodJournal AI
            </h1>
            <p className="text-lg text-gray-700 mb-6 font-raleway text-center max-w-xl mx-auto">
              Your personal AI-powered diary for emotional clarity, memories, and reflection.
            </p>

            {/* Polaroid style images */}
            <div className="flex flex-wrap justify-center gap-6 mb-6">
              <div className="bg-white shadow-lg p-2 rotate-[-6deg]">
                <img src="/polaroid1.jpg" alt="Memory 1" className="w-40 h-40 object-cover" />
                <p className="text-xs text-center mt-1">Day at the park</p>
              </div>
              <div className="bg-white shadow-lg p-2 rotate-3">
                <img src="/polaroid2.jpg" alt="Memory 2" className="w-40 h-40 object-cover" />
                <p className="text-xs text-center mt-1">Cozy evening</p>
              </div>
              <div className="bg-white shadow-lg p-2 rotate-[-3deg]">
                <img src="/polaroid3.jpg" alt="Memory 3" className="w-40 h-40 object-cover" />
                <p className="text-xs text-center mt-1">Sunset walk</p>
              </div>
            </div>

            {/* Current feature */}
            <motion.div
              className="text-2xl text-center text-indigo-600 font-semibold font-raleway"
              key={currentPage}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              {features[currentPage]}
            </motion.div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default Home;
