import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import './NavBar.css';

const NavBar = ({ user, onLogout }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 20;
      setScrolled(isScrolled);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleLogout = () => {
    if (onLogout) {
      onLogout();
    }
    navigate('/');
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="nav-container">
        <Link to="/" className="nav-logo">
          <div className="logo-container">
            <span className="logo-icon">üå∏</span>
            <span className="logo-text font-handwriting">SafeSpace</span>
          </div>
        </Link>

        <div className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
          {user ? (
            <>
              <div className="nav-links">
                <Link 
                  to={`/${user.username}`} 
                  className={`nav-link ${isActive(`/${user.username}`) ? 'active' : ''}`}
                >
                  <span className="nav-icon">üè†</span>
                  <span className="nav-text">Dashboard</span>
                </Link>
                <Link 
                  to={`/${user.username}/entry`} 
                  className={`nav-link ${isActive(`/${user.username}/entry`) ? 'active' : ''}`}
                >
                  <span className="nav-icon">üìù</span>
                  <span className="nav-text">My Journals</span>
                </Link>
                <Link 
                  to={`/${user.username}/insights`} 
                  className={`nav-link ${isActive(`/${user.username}/insights`) ? 'active' : ''}`}
                >
                  <span className="nav-icon">üìä</span>
                  <span className="nav-text">Insights</span>
                </Link>
                <Link 
                  to="/community" 
                  className={`nav-link ${isActive('/community') ? 'active' : ''}`}
                >
                  <span className="nav-icon">üèòÔ∏è</span>
                  <span className="nav-text">Community</span>
                </Link>
                <Link 
                  to="/affirmations" 
                  className={`nav-link ${isActive('/affirmations') ? 'active' : ''}`}
                >
                  <span className="nav-icon">‚ú®</span>
                  <span className="nav-text">Affirmations</span>
                </Link>
                <Link 
                  to={`/${user.username}/manifestations`} 
                  className={`nav-link ${isActive(`/${user.username}/manifestations`) ? 'active' : ''}`}
                >
                  <span className="nav-icon">üåü</span>
                  <span className="nav-text">Manifestations</span>
                </Link>
                <Link 
                  to="/features" 
                  className={`nav-link ${isActive('/features') ? 'active' : ''}`}
                >
                  <span className="nav-icon">üöÄ</span>
                  <span className="nav-text">Features</span>
                </Link>
                <Link 
                  to="/contact" 
                  className={`nav-link ${isActive('/contact') ? 'active' : ''}`}
                >
                  <span className="nav-icon">üìû</span>
                  <span className="nav-text">Contact</span>
                </Link>
              </div>

              <div className="user-section">
                <div className="user-info">
                  <div className="user-avatar-container">
                    <div className="user-avatar">
                      {user.username.charAt(0).toUpperCase()}
                    </div>
                    <div className="user-level-badge">
                      Lv.{user.level || 1}
                    </div>
                  </div>
                  <div className="user-details">
                    <span className="user-name font-handwriting">{user.username}</span>
                    <div className="user-stats">
                      <span className="stat">
                        <span className="stat-icon">‚≠ê</span>
                        {user.points || 0}
                      </span>
                      <span className="stat">
                        <span className="stat-icon">üî•</span>
                        {user.streak || 0}
                      </span>
                    </div>
                  </div>
                </div>
                
                <div className="user-actions">
                  <button 
                    onClick={handleLogout} 
                    className="logout-btn"
                    title="Logout"
                  >
                    <span className="logout-icon">üö™</span>
                  </button>
                </div>
              </div>
            </>
          ) : (
            <>
              <div className="auth-links">
                <Link to="/login" className="nav-button login-btn">
                  <span className="btn-icon">üîë</span>
                  <span className="btn-text">Login</span>
                </Link>
                <Link to="/signin" className="nav-button signup-btn">
                  <span className="btn-icon">‚ú®</span>
                  <span className="btn-text">Sign Up</span>
                </Link>
              </div>
            </>
          )}
        </div>

        <div 
          className={`hamburger ${isMenuOpen ? 'active' : ''}`}
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          <span></span>
          <span></span>
          <span></span>
        </div>
      </div>
    </nav>
  );
};

export default NavBar;
