import { useState } from 'react';
import './App.css';
import './output.css';
import NavBar from './NavBar';
import Home from './Home';
import Footer from './Footer';
import Entry from './Entry';
import Layout2 from './Layout2'; // ðŸ©· Import Layout2 for Affirmations
import Signin from './SignIn';
import Login from './Login';
import Layout1 from './Layout1'; // ðŸ©· Import Layout1 for Journals
import Layout from './Layout';
import { BrowserRouter, Routes, Route } from 'react-router-dom';

function App() {
  const [user, setUser] = useState(null); // ðŸ©· track logged-in user

  return (
    <BrowserRouter>
      <div className="first-pg flex flex-col min-h-screen">
        <NavBar user={user} /> {/* ðŸ”· Pass user here */}
        <main className="flex-grow">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/home" element={<Home />} />
            <Route path="/signin" element={<Signin />} />
            <Route
              path="/login"
              element={
                <Login
                  onLogin={() =>
                    setUser({ name: 'Jane Doe', pfp: '' }) // dummy login
                  }
                />
              }
            />
            <Route path="/dashboard" element={<Layout />} />
            <Route path="/journals" element={<Layout1 />} />
            <Route path="/entry" element={<Entry />} />
            <Route
              path="/affirmations"
              element={<Layout2 />}
            />
            <Route
              path="*"
              element={
                <div className="text-center py-20 text-pink-600 text-xl">
                  404 â€” Page Not Found
                </div>
              }
            />
          </Routes>
        </main>
      </div>
      <Footer />
    </BrowserRouter>
  );
}

export default App;
