import { motion } from "framer-motion";
const features = [
  "🧠 AI-powered Emotion Insights",
  "📈 Mood Trend Visualization",
  "✍️ Daily Journaling",
  "🔐 Private & Secure",
  "🎨 Calming UI & Experience",
  "💬 Emotional Support from SereneAI",
];
const stats = [
  { number: "10K+", label: "Journals Written" },
  { number: "5K+", label: "Happy Users" },
  { number: "99.9%", label: "Data Privacy" },
];
const Home = () => {
  return (

    <div className="flex flex-row md:flex-row min-h-[calc(100vh-5rem)] bg-transparent font-figtree relative">
      {/* LEFT — Landing */}
   <div className="flex flex-row md:flex-row min-h-[calc(100vh-5rem)] bg-transparent font-figtree relative justify-center items-center">
    <img src={"./mainimg.png"} alt="Main journal illustration" height="520" width="520" />

</div>



      <div className="flex-1 flex items-center h-full flex-col justify-center p-6">
        <div className="relative w-full max-w-md rounded-2xl shadow-xl border border-pink-100 overflow-hidden bg-white bg-opacity-80">
          <div className="absolute inset-0 bg-[linear-gradient(to_bottom,transparent_94%,#e5e7eb_94%)] bg-[length:100%_2rem] z-0 pointer-events-none"></div>

          <div className="relative px-6 py-10 z-10">
            <h1
  className="
    text-4xl mb-4 text-center font-bold cursor-pointer
    transition-transform duration-700 ease-in-out
    transform hover:scale-105
  "
  style={{
    fontFamily: "'Irish Grover', system-ui",
    color: '#f8cce5', // pastel baby pink
    WebkitTextStroke: '1px #fffff', // soft pink border/stroke
    textShadow: `
      0 0 4px rgba(248, 204, 229, 0.8),
      0 0 10px rgba(248, 204, 229, 0.6),
      0 0 20px rgba(248, 204, 229, 0.4)
    `,
    transition: 'filter 0.7s ease-in-out, transform 0.7s ease-in-out',
  }}
  onMouseEnter={(e) => {
    e.currentTarget.style.filter = 'brightness(1.2) saturate(1.3)';
  }}
  onMouseLeave={(e) => {
    e.currentTarget.style.filter = 'brightness(1) saturate(1)';
  }}
>
              SafeSpace
            </h1>
            <p className="text-base text-gray-700 mb-8 font-raleway text-center">
              Your personal AI-powered diary for emotional clarity and calm reflection.
            </p>

            <motion.ul className="space-y-4">
              {features.map((feature, index) => (
                <motion.li
                  key={index}
                  className=" text-left text-indigo-600 font-semibold text-2.5xl"
                  style={{ fontFamily: "'The Girl Next Door', cursive" }}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: index * 0.3, duration: 0.4 }}
                >
                  {feature}
                </motion.li>
              ))}
            </motion.ul>
          </div>
        </div>
      </div>

      

      {/* RIGHT — Website Stats */}
      <div className="flex-1 flex items-center justify-center p-6">
        <div className="bg-white bg-opacity-80 rounded-2xl shadow-xl p-8 max-w-md w-full">
          <h2 className="text-3xl text-center text-pink-500 mb-6 font-sacramento">
            📊 Our Impact
          </h2>
          <p className="text-center text-gray-600 mb-8 font-raleway">
            Helping thousands reflect, grow and find emotional clarity every day.
          </p>

          <div className="grid grid-cols-1 gap-6">
            {stats.map((stat, idx) => (
              <div
                key={idx}
                className="bg-pink-50 rounded-lg p-4 text-center shadow hover:shadow-md transition"
              >
                <div className="text-2xl text-pink-600 font-bold">{stat.number}</div>
                <div className="text-sm text-gray-600">{stat.label}</div>
              </div>
            ))}
          </div>

          <div className="mt-8 text-center">
            <a
              href="/next"
              className="inline-block px-6 py-3 rounded-full bg-pink-500 text-white font-semibold hover:bg-pink-600 transition"
            >
              Get Started Now
            </a>
          </div>
        </div>
      </div>
    </div>

  );
};
export default Home;
