import React, { useState, useRef, useEffect } from 'react';
import { motion, useAnimation } from 'framer-motion';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Sphere } from '@react-three/drei';
import './App.css'; // For custom styles if needed

const FeatureCard = ({ title, description, color, image, index }) => {
  const controls = useAnimation();

  useEffect(() => {
    const handleScroll = () => {
      const element = document.getElementById(`feature-${index}`);
      if (element) {
        const rect = element.getBoundingClientRect();
        const isInView = rect.top >= 0 && rect.top <= window.innerHeight;
        if (isInView) {
          controls.start({ opacity: 1, y: 0, transition: { duration: 0.8 } });
        }
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [controls, index]);

  return (
    <motion.div
      id={`feature-${index}`}
      className="w-full py-20 text-center relative"
      initial={{ opacity: 0, y: 100 }}
      animate={controls}
    >
      <div
        className="rounded-3xl p-6 shadow-lg inline-block transform hover:scale-105 transition-transform duration-500"
        style={{ backgroundColor: color, border: '2px solid #FFC1CC' }}
      >
        <img src={image} alt={title} className="w-96 h-64 object-cover rounded-2xl" />
        <h2 className="text-5xl font-sacramento text-white mt-6">{title}</h2>
        <p className="text-xl text-white mt-4">{description}</p>
      </div>
      <motion.div
        className="absolute top-1/2 left-10 w-20 h-20"
        animate={{ rotate: 360 }}
        transition={{ duration: 10, repeat: Infinity, ease: "linear" }}
        style={{ backgroundColor: color, borderRadius: '50%' }}
      />
    </motion.div>
  );
};

const Carousel = () => {
  const carouselRef = useRef(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const images = [
    "https://in.pinterest.com/pin/462604192987630275/",
    "https://in.pinterest.com/pin/462604192981040914/",
    "https://in.pinterest.com/pin/462604192981019649/",
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % images.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <div className="w-full h-96 relative overflow-hidden mb-20">
      <motion.div
        ref={carouselRef}
        className="flex transition-transform duration-1000 ease-in-out"
        style={{ transform: `translateX(-${currentIndex * 100}%)` }}
      >
        {images.map((img, index) => (
          <div key={index} className="w-full h-full flex-shrink-0">
            <img src={img} alt={`Slide ${index + 1}`} className="w-full h-full object-cover rounded-3xl shadow-lg" />
          </div>
        ))}
      </motion.div>
      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
        {images.map((_, index) => (
          <span
            key={index}
            className={`h-3 w-3 rounded-full ${currentIndex === index ? 'bg-pink-500' : 'bg-gray-300'}`}
            onClick={() => setCurrentIndex(index)}
          />
        ))}
      </div>
    </div>
  );
};

const ThreeDEffect = () => (
  <Canvas className="absolute top-0 left-0 w-full h-full pointer-events-none" style={{ zIndex: -1 }}>
    <ambientLight intensity={0.5} />
    <pointLight position={[10, 10, 10]} />
    <Sphere args={[1, 32, 32]}>
      <meshStandardMaterial color="#3C2F2F" metalness={0.5} roughness={0.5} />
    </Sphere>
    <OrbitControls enableZoom={false} enablePan={false} enableRotate={false} />
  </Canvas>
);

const Features = () => {
  const features = [
    { title: 'Mood Journaling', description: 'Track your daily emotions with ease.', color: '#FFC1CC', image: 'https://in.pinterest.com/pin/462604192987630275/' },
    { title: 'Affirmations Board', description: 'Boost positivity with custom affirmations.', color: '#F8AFA8', image: 'https://in.pinterest.com/pin/462604192981040914/' },
    { title: 'Community Hub', description: 'Connect anonymously with others.', color: '#D7B9D5', image: 'https://in.pinterest.com/pin/462604192981019649/' },
    { title: 'Insight Dashboard', description: 'Visualize your mood trends.', color: '#A3BFFA', image: 'https://in.pinterest.com/pin/462604192980942018/' },
  ];

  return (
    <div className="min-h-screen relative overflow-hidden">
      <ThreeDEffect />
      <div className="container mx-auto px-6 py-12">
        <h1 className="text-6xl font-sacramento text-3C2F2F text-center mb-16">ðŸŒ¸ Discover Features ðŸŒ¸</h1>
        <Carousel />
        {features.map((feature, index) => (
          <FeatureCard key={index} {...feature} index={index} />
        ))}
      </div>
    </div>
  );
};

export default Features;
