import React, { useState } from 'react';
import { FaSearch, FaSort } from 'react-icons/fa'; // For icons

const MyJournals = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('date');

  // Sample journal data (replace with your data source)
  const journals = [
    { id: 1, title: 'Happy Day', date: '2025-06-20', moodPoints: 8, content: 'Today was amazing!' },
    { id: 2, title: 'Sad Moment', date: '2025-06-15', moodPoints: 2, content: 'Feeling low today.' },
    { id: 3, title: 'Exciting Trip', date: '2025-06-10', moodPoints: 7, content: 'Went on an adventure!' },
    { id: 4, title: 'Quiet Evening', date: '2025-06-05', moodPoints: 5, content: 'Relaxed at home.' },
  ];

  // Filter journals based on search term
  const filteredJournals = journals.filter(journal =>
    journal.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    journal.content.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Sort journals based on selected option
  const sortedJournals = [...filteredJournals].sort((a, b) => {
    if (sortOption === 'alphabet') return a.title.localeCompare(b.title);
    if (sortOption === 'mood') return b.moodPoints - a.moodPoints;
    return new Date(b.date) - new Date(a.date); // Default: sort by date
  });

  return (
    <div className="min-h-screen bg-gradient-to-b from-pink-100 to-pink-200 p-6 font-nunito">
      <h1 className="text-4xl font-sacramento text-pink-600 text-center mb-6 animate-pulse">ðŸŒ¸ My Journals ðŸŒ¸</h1>

      {/* Search and Sort Section */}
      <div className="flex justify-between mb-6">
        <div className="relative w-1/2">
          <input
            type="text"
            placeholder="Search journals..."
            className="w-full p-3 rounded-full border-2 border-pink-300 focus:outline-none focus:ring-2 focus:ring-pink-400 text-pink-800 placeholder-pink-400 transition-all duration-300 ease-in-out"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <FaSearch className="absolute right-3 top-3 text-pink-500" />
        </div>
        <div className="relative">
          <select
            className="p-3 rounded-full border-2 border-pink-300 focus:outline-none focus:ring-2 focus:ring-pink-400 text-pink-800 bg-white cursor-pointer transition-all duration-300 ease-in-out"
            value={sortOption}
            onChange={(e) => setSortOption(e.target.value)}
          >
            <option value="date">Sort by Date</option>
            <option value="alphabet">Sort by Alphabet</option>
            <option value="mood">Sort by Mood</option>
          </select>
          <FaSort className="absolute right-3 top-3 text-pink-500" />
        </div>
      </div>

      {/* Journals Display */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {sortedJournals.map(journal => (
          <div
            key={journal.id}
            className="bg-pink-50 p-4 rounded-xl shadow-md hover:shadow-lg hover:scale-105 transition-all duration-300 ease-in-out border-2 border-pink-200"
          >
            <h2 className="text-xl font-sacramento text-pink-600 mb-2">{journal.title}</h2>
            <p className="text-sm text-pink-800 mb-1">Date: {new Date(journal.date).toLocaleDateString()}</p>
            <p className="text-sm text-pink-800 mb-1">Mood: {journal.moodPoints}/10 ðŸŒ¸</p>
            <p className="text-gray-600 text-sm">{journal.content}</p>
          </div>
        ))}
      </div>

      {/* Write Now Button */}
      <div className="text-center mt-8">
        <a
          href="/entry" // Replace with your entry page route
          className="inline-block bg-gradient-to-r from-pink-300 to-pink-400 text-white font-bold py-3 px-6 rounded-full shadow-md hover:bg-pink-500 hover:scale-110 transition-all duration-300 ease-in-out text-lg animate-bounce"
        >
          âœ¨ Write Now! ðŸŒ¸
        </a>
      </div>
    </div>
  );
};

export default MyJournals;
