import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend, BarChart, Bar } from 'recharts';
import './Dashboard.css';
import ActivityHeatmap from '../ActivityHeatmap';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const Dashboard = ({ user }) => {
  const { username } = useParams();
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [recentEntries, setRecentEntries] = useState([]);
  const [moodTrends7, setMoodTrends7] = useState([]);
  const [moodTrends180, setMoodTrends180] = useState([]);
  const [activityData, setActivityData] = useState([]);
  const [calendarStreaks, setCalendarStreaks] = useState([]);

  const API_BASE_URL = 'http://localhost:5001/api';

  // Fetch dashboard data
  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      
      // Fetch dashboard stats
      const statsResponse = await fetch(`${API_BASE_URL}/user/dashboard`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Fetch recent entries
      const entriesResponse = await fetch(`${API_BASE_URL}/journal?limit=5&sortBy=date&sortOrder=desc`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (statsResponse.ok && entriesResponse.ok) {
        const statsData = await statsResponse.json();
        const entriesData = await entriesResponse.json();
        
        setDashboardData(statsData.data);
        setRecentEntries(entriesData.data.entries);
      } else {
        setError('Failed to fetch dashboard data');
      }
    } catch (error) {
      setError('Network error');
    } finally {
      setLoading(false);
    }
  };

  // Fetch mood trends and activity heatmap data
  useEffect(() => {
    const token = localStorage.getItem('token');
    // Fetch 7-day mood trends
    fetch(`${API_BASE_URL}/insights/mood?period=7`, {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setMoodTrends7(data.data?.intensityTrends || []));
    // Fetch 6-month mood trends
    fetch(`${API_BASE_URL}/insights/mood?period=180`, {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setMoodTrends180(data.data?.intensityTrends || []));
    // Fetch activity heatmap (past year)
    fetch(`${API_BASE_URL}/insights/patterns?period=365`, {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setActivityData(data.data?.entries || []));
    // Fetch calendar streaks (reuse activity data for now)
    // You can enhance this to highlight streaks specifically
    fetch(`${API_BASE_URL}/insights/patterns?period=365`, {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setCalendarStreaks(data.data?.entries || []));
  }, []);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 to-green-50 flex items-center justify-center">
        <div className="spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 to-green-50 p-6">
        <div className="max-w-6xl mx-auto">
          <div className="error-message">
            <h2 className="text-xl font-semibold mb-2">❌ Error Loading Dashboard</h2>
            <p>{error}</p>
            <button onClick={fetchDashboardData} className="btn-primary mt-4">
              🔄 Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  const getLevelInfo = (level) => {
    const levels = {
      1: { name: 'Novice Journaler', color: '#6b7280', icon: '🌱' },
      2: { name: 'Mindful Writer', color: '#10b981', icon: '📝' },
      3: { name: 'Emotional Explorer', color: '#3b82f6', icon: '🔍' },
      4: { name: 'Wellness Warrior', color: '#8b5cf6', icon: '⚔️' },
      5: { name: 'Sage of Self', color: '#f59e0b', icon: '🧘' },
      6: { name: 'Master of Mindfulness', color: '#ec4899', icon: '🌟' }
    };
    return levels[level] || levels[1];
  };

  const getMoodEmoji = (mood) => {
    const emojis = {
      happy: '😊',
      sad: '😢',
      excited: '🤩',
      calm: '😌',
      anxious: '😰',
      joyful: '😄',
      tired: '😴',
      neutral: '😐'
    };
    return emojis[mood] || '😐';
  };

  const levelInfo = getLevelInfo(dashboardData?.level || 1);

  // Example mood and happiness data (replace with backend data)
  const moodData = dashboardData?.moodTrends || [
    { date: 'Mon', mood: 3 },
    { date: 'Tue', mood: 4 },
    { date: 'Wed', mood: 2 },
    { date: 'Thu', mood: 5 },
    { date: 'Fri', mood: 4 },
    { date: 'Sat', mood: 3 },
    { date: 'Sun', mood: 5 },
  ];
  const happinessData = dashboardData?.happinessTrends || [
    { date: 'Mon', happiness: 60 },
    { date: 'Tue', happiness: 70 },
    { date: 'Wed', happiness: 50 },
    { date: 'Thu', happiness: 80 },
    { date: 'Fri', happiness: 75 },
    { date: 'Sat', happiness: 65 },
    { date: 'Sun', happiness: 90 },
  ];

  return (
    <div className="dashboard-rows-container">
      {/* Row 1: Stats */}
      <div className="dashboard-row">
        <div className="stats-row">
          <div className="stats-card">
            <div className="stats-icon">📝</div>
            <h3 className="text-lg font-semibold mb-1">Total Entries</h3>
            <p className="stats-number">{dashboardData?.totalEntries || 0}</p>
          </div>
          
          <div className="stats-card">
            <div className="stats-icon">⭐</div>
            <h3 className="text-lg font-semibold mb-1">Total Points</h3>
            <p className="stats-number">{dashboardData?.totalPoints || 0}</p>
          </div>
          
          <div className="stats-card">
            <div className="stats-icon">🔥</div>
            <h3 className="text-lg font-semibold mb-1">Current Streak</h3>
            <p className="stats-number">{dashboardData?.currentStreak || 0} days</p>
          </div>
          
          <div className="stats-card">
            <div className="stats-icon">📈</div>
            <h3 className="text-lg font-semibold mb-1">Average Points</h3>
            <p className="stats-number">{dashboardData?.averagePoints?.toFixed(1) || 0}</p>
          </div>
        </div>
      </div>
      {/* Row 2: Level & Progress */}
      <div className="dashboard-row">
        <div className="card">
          <h2 className="text-2xl font-bold mb-4 gradient-text">🎯 Your Level & Progress</h2>
          <div className="text-center p-6">
            <div className="text-6xl mb-4">{levelInfo.icon}</div>
            <h3 className="text-xl font-semibold mb-2" style={{ color: levelInfo.color }}>
              {levelInfo.name}
            </h3>
            <p className="text-gray-600 mb-4">Level {dashboardData?.level || 1}</p>
            
            {/* Progress Bar */}
            <div className="w-full bg-gray-200 rounded-full h-4 mb-4">
              <div 
                className="h-4 rounded-full transition-all duration-500"
                style={{ 
                  width: `${((dashboardData?.totalPoints || 0) % 1000) / 10}%`,
                  background: `linear-gradient(90deg, ${levelInfo.color}, #ec4899)`
                }}
              ></div>
            </div>
            
            <p className="text-sm text-gray-600">
              {dashboardData?.totalPoints || 0} / {Math.ceil((dashboardData?.totalPoints || 0) / 1000) * 1000} points to next level
            </p>
          </div>
        </div>
      </div>
      {/* Row 3: Recent Entries */}
      <div className="dashboard-row">
        <div className="card mb-8">
          <h2 className="text-2xl font-bold mb-4 gradient-text">📝 Recent Entries</h2>
          {recentEntries.length === 0 ? (
            <div className="text-center py-8">
              <div className="text-4xl mb-4">📝</div>
              <p className="text-gray-600 mb-4">No entries yet. Start your journaling journey!</p>
              <button className="btn-primary">✨ Write Your First Entry</button>
            </div>
          ) : (
            <div className="space-y-4">
              {recentEntries.map((entry) => (
                <div key={entry._id} className="entry-preview">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <h3 className="font-semibold text-lg mb-1">{entry.title}</h3>
                      <p className="text-gray-600 text-sm mb-2">
                        {new Date(entry.createdAt).toLocaleDateString()}
                      </p>
                      <p className="text-gray-700 line-clamp-2">{entry.content}</p>
                    </div>
                    <div className="flex items-center gap-2 ml-4">
                      <span className="text-lg">{getMoodEmoji(entry.mood?.primary)}</span>
                      <span className="points-badge">⭐ {entry.aiAnalysis?.pointsEarned || 0}</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
      {/* Row 4: Charts (Mood Trends, Happiness, etc.) */}
      <div className="dashboard-row dashboard-charts-row">
        <div className="dashboard-chart">
          <h3 className="chart-title">Mood Trend (7 days)</h3>
          <ResponsiveContainer width="100%" height={180}>
            <LineChart data={moodTrends7.map(d => ({ date: new Date(d.date).toLocaleDateString(), mood: d.intensity }))}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis domain={[1, 5]} />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="mood" stroke="#ec4899" strokeWidth={3} dot={{ r: 5 }} />
            </LineChart>
          </ResponsiveContainer>
        </div>
        <div className="dashboard-chart">
          <h3 className="chart-title">Mood Trend (6 months)</h3>
          <ResponsiveContainer width="100%" height={180}>
            <LineChart data={moodTrends180.map(d => ({ date: new Date(d.date).toLocaleDateString(), mood: d.intensity }))}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis domain={[1, 5]} />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="mood" stroke="#a259c6" strokeWidth={3} dot={{ r: 5 }} />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>
      {/* Row 5: Activity Heatmap */}
      <div className="dashboard-row">
        <h3 className="chart-title">Activity Heatmap</h3>
        <ActivityHeatmap data={activityData} />
      </div>
      {/* Row 6: Calendar Streaks */}
      <div className="dashboard-row">
        <h3 className="chart-title">Calendar Streaks</h3>
        <Calendar
          tileContent={({ date, view }) => {
            if (view === 'month') {
              const dateStr = date.toISOString().split('T')[0];
              const isStreak = calendarStreaks.some(entry => new Date(entry.createdAt).toISOString().split('T')[0] === dateStr);
              return isStreak ? <div className="calendar-streak-dot" /> : null;
            }
            return null;
          }}
        />
      </div>
      {/* Row 7: Insights */}
      <div className="dashboard-row dashboard-insights">
        <h3 className="insights-title">AI Insights & Trends</h3>
        <div className="insights-content">
          {dashboardData?.aiInsights || 'Your mood is improving! Keep journaling for more positive trends.'}
        </div>
      </div>
      {/* Row 8: Quick Actions */}
      <div className="dashboard-row dashboard-actions-row wide">
        <button className="btn-primary action-btn wide">✨ New Entry</button>
        <button className="btn-primary action-btn wide">🤖 Chat with Dr. Luna</button>
        <button className="btn-primary action-btn wide">📊 View Insights</button>
      </div>
      {/* Row 9: Motivational Quote */}
      {dashboardData?.motivationalQuote && (
        <div className="card mt-8 text-center">
          <div className="text-3xl mb-4">💭</div>
          <blockquote className="text-lg italic text-gray-700 mb-2">
            "{dashboardData.motivationalQuote.text}"
          </blockquote>
          <cite className="text-sm text-gray-600">— {dashboardData.motivationalQuote.author}</cite>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
