import React, { useState } from 'react';

const Affirmations = () => {
  const [affirmation, setAffirmation] = useState('');
  const [affirmationsList, setAffirmationsList] = useState([
    'I am doing my best and that is enough.',
    'I accept my thoughts and emotions.',
    'Everyone makes mistakes. I can try again.',
    'I am unique and capable of great things.',
    'I can learn and grow every day.',
    'I can achieve my goals.',
    'I will be patient with myself.',
    'I will encourage myself.',
  ]);
  const [editIndex, setEditIndex] = useState(null);
  const [editText, setEditText] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (affirmation.trim()) {
      setAffirmationsList([...affirmationsList, affirmation.trim()]);
      setAffirmation('');
    }
  };

  const handleEdit = (index) => {
    setEditIndex(index);
    setEditText(affirmationsList[index]);
  };

  const handleSave = (index) => {
    const newList = [...affirmationsList];
    newList[index] = editText.trim();
    setAffirmationsList(newList);
    setEditIndex(null);
    setEditText('');
  };

  const handleDelete = (index) => {
    const newList = affirmationsList.filter((_, i) => i !== index);
    setAffirmationsList(newList);
  };

  // Total cells in a 5x6 table (30 cells)
  const totalCells = 5 * 6;
  const visibleCells = affirmationsList.length;

  return (
    <div className="min-h-screen bg-gradient-to-b from-pink-100 to-pink-200 p-6 font-nunito">
      <h1 className="text-4xl font-sacramento text-pink-600 text-center mb-8 animate-pulse">üå∏ My Affirmations Board üå∏</h1>

      {/* Add Affirmation Form */}
      <div className="max-w-2xl mx-auto mb-8">
        <form onSubmit={handleSubmit} className="flex space-x-4">
          <input
            type="text"
            value={affirmation}
            onChange={(e) => setAffirmation(e.target.value)}
            placeholder="Add your affirmation..."
            className="flex-1 p-3 rounded-full border-2 border-pink-300 focus:outline-none focus:ring-2 focus:ring-pink-400 text-pink-800 placeholder-pink-400 transition-all duration-300 ease-in-out"
          />
          <button
            type="submit"
            className="bg-gradient-to-r from-pink-300 to-pink-400 text-white font-bold py-3 px-6 rounded-full shadow-md hover:bg-pink-500 hover:scale-110 transition-all duration-300 ease-in-out"
          >
            ‚ú® Add üå∏
          </button>
        </form>
      </div>

      {/* Affirmations Table */}
      <div className="max-w-3xl mx-auto overflow-x-auto">
        <table className="w-full table-fixed border-collapse">
          <tbody>
            {[...Array(5)].map((_, rowIndex) => (
              <tr key={rowIndex} className="border-b border-pink-200">
                {[...Array(6)].map((_, colIndex) => {
                  const index = rowIndex * 6 + colIndex;
                  if (index < visibleCells) {
                    return (
                      <td
                        key={colIndex}
                        className="p-2 align-middle"
                        style={{ width: '16.666%' }} // Equal width for 6 columns (100% / 6)
                      >
                        <div
                          className="bg-opacity-90 p-4 rounded-lg shadow-md hover:shadow-lg hover:scale-105 transition-all duration-300 ease-in-out text-center h-60 w-60 flex items-center justify-center m-2 overflow-hidden relative"
                          style={{
                            backgroundColor: ['#A8B5C7', '#D4A5A5', '#E8C4B0', '#B7D7D8', '#C9A7A4', '#B0C4DE'][index % 6],
                          }}
                        >
                          <div className="flex flex-col items-center">
                            {index % 6 === 0 && <span role="img" aria-label="sun" className="text-3xl mb-2">‚òÄÔ∏è</span>}
                            {index % 6 === 1 && <span role="img" aria-label="rainbow" className="text-3xl mb-2">üåà</span>}
                            {index % 6 === 2 && <span role="img" aria-label="heart" className="text-3xl mb-2">‚ù§Ô∏è</span>}
                            {index % 6 === 3 && <span role="img" aria-label="moon" className="text-3xl mb-2">üåô</span>}
                            {index % 6 === 4 && <span role="img" aria-label="flower" className="text-3xl mb-2">üå∏</span>}
                            {index % 6 === 5 && <span role="img" aria-label="tree" className="text-3xl mb-2">üå≥</span>}
                            {editIndex === index ? (
                              <div className="w-full">
                                <input
                                  type="text"
                                  value={editText}
                                  onChange={(e) => setEditText(e.target.value)}
                                  className="w-full p-1 text-center text-pink-800 rounded"
                                />
                                <button
                                  onClick={() => handleSave(index)}
                                  className="mt-2 bg-green-300 text-white font-bold py-1 px-2 rounded-full hover:bg-green-400 text-sm"
                                >
                                  Save
                                </button>
                              </div>
                            ) : (
                              <p className="text-pink-800 font-semibold text-sm break-words overflow-hidden text-ellipsis line-clamp-3">{affirmationsList[index]}</p>
                            )}
                          </div>
                          {editIndex !== index && (
                            <div className="absolute top-2 right-2 flex space-x-2">
                              <button
                                onClick={() => handleEdit(index)}
                                className="text-blue-500 hover:text-blue-700 text-sm"
                              >
                                ‚úèÔ∏è
                              </button>
                              <button
                                onClick={() => handleDelete(index)}
                                className="text-red-500 hover:text-red-700 text-sm"
                              >
                                üóëÔ∏è
                              </button>
                            </div>
                          )}
                        </div>
                      </td>
                    );
                  }
                  return null; // Hide unfilled cells
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Affirmations;
