// src/components/Community.js

import React, { useState, useEffect, useCallback } from 'react';
import './Community.css'; // Make sure you have the corresponding CSS file

// Mock user for demonstration - in a real app, this would come from an Auth Context
const MOCK_USER = { _id: 'user123', username: 'GratefulSoul', profilePicture: 'üôè' };

const Community = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // State for filtering, sorting, and searching
  const [sortBy, setSortBy] = useState('latest');
  const [filter, setFilter] = useState('all'); // 'all' or 'my-posts'
  const [category, setCategory] = useState('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [debouncedSearch, setDebouncedSearch] = useState('');

  const [newPost, setNewPost] = useState({ title: '', content: '', category: 'general' });
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Debounce search input to avoid excessive API calls
  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedSearch(searchQuery);
    }, 500); // 500ms delay
    return () => clearTimeout(handler);
  }, [searchQuery]);

  // Main data fetching function
  const fetchPosts = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      let url = `/api/community?sort=${sortBy}&category=${category}`;
      if (filter === 'my-posts') {
        url += `&authorId=${MOCK_USER._id}`;
      }
      if (debouncedSearch) {
        url += `&search=${debouncedSearch}`;
      }
      
      const response = await fetch(url);
      if (!response.ok) throw new Error('Failed to fetch posts.');
      
      const result = await response.json();
      setPosts(result.data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [sortBy, category, filter, debouncedSearch]);

  useEffect(() => {
    fetchPosts();
  }, [fetchPosts]);

  const handleLike = async (postId) => {
    // Optimistic UI update
    const originalPosts = [...posts];
    const updatedPosts = posts.map(p => {
        if (p._id === postId) {
            const isLiked = !p.isLiked;
            return { ...p, isLiked, likeCount: isLiked ? p.likeCount + 1 : p.likeCount - 1 };
        }
        return p;
    });
    setPosts(updatedPosts);
    
    // API call
    try {
        await fetch(`/api/community/${postId}/like`, { method: 'PATCH' });
        // Optionally refetch or trust the optimistic update
    } catch (err) {
        setPosts(originalPosts); // Revert on error
        alert('Failed to update like.');
    }
  };

  const handleSubmitPost = async (e) => {
    e.preventDefault();
    if (newPost.title.trim() && newPost.content.trim()) {
      setIsSubmitting(true);
      try {
        const response = await fetch('/api/community', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newPost),
        });
        if (!response.ok) throw new Error('Failed to submit post.');
        
        const result = await response.json();
        setPosts([result.data, ...posts]);
        setNewPost({ title: '', content: '', category: 'general' });
      } catch (err) {
        alert(err.message);
      } finally {
        setIsSubmitting(false);
      }
    }
  };

  const handleDeletePost = async (postId) => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      try {
        const response = await fetch(`/api/community/${postId}`, { method: 'DELETE' });
        if (!response.ok) throw new Error('Failed to delete post.');
        
        setPosts(posts.filter(p => p._id !== postId));
      } catch (err) {
        alert(err.message);
      }
    }
  };
  
  const categories = [
    { id: 'all', name: 'All Posts', icon: 'üì∞' },
    { id: 'mental-health', name: 'Mental Health', icon: 'üß†' },
    { id: 'gratitude', name: 'Gratitude', icon: 'üôè' },
    { id: 'writing', name: 'Writing Tips', icon: '‚úçÔ∏è' },
    { id: 'lifestyle', name: 'Lifestyle', icon: 'üåü' }
  ];

  return (
    <div className="community-container">
      {/* --- Header remains the same --- */}
      <div className="community-header">
         {/* ... existing header JSX ... */}
      </div>

      <div className="community-content">
        <div className="community-sidebar">
           {/* New Search Bar */}
           <div className="sidebar-section">
              <h3 className="section-title">Search Posts</h3>
              <div className="search-container">
                <span className="search-icon">üîç</span>
                <input
                  type="text"
                  placeholder="Find stories, tips..."
                  className="search-input"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </div>
            </div>

           {/* Updated Filters */}
           <div className="sidebar-section">
             <h3 className="section-title">Filter By</h3>
             <div className="filter-options">
               <button className={`filter-option ${filter === 'all' ? 'active' : ''}`} onClick={() => setFilter('all')}>All Posts</button>
               <button className={`filter-option ${filter === 'my-posts' ? 'active' : ''}`} onClick={() => setFilter('my-posts')}>My Posts</button>
             </div>
           </div>
           
           {/* Categories and Sort By sections remain similar */}
           {/* ... existing sidebar sections for categories and sort ... */}
        </div>

        <div className="community-main">
          {/* Create Post Section remains similar */}
          {/* ... existing create post form JSX ... */}

          <div className="posts-section">
            {/* Posts List */}
            {loading ? (
              <div className="loading-container"><div className="spinner"></div></div>
            ) : error ? (
              <div className="error-state">{error}</div>
            ) : posts.length > 0 ? (
              <div className="posts-list">
                {posts.map(post => (
                  <div key={post.id || post._id} className="post-card">
                    <div className="post-header">
                        <div className="post-author">
                            <div className="author-avatar">{post.author.profilePicture || post.author.username.charAt(0)}</div>
                            <div className="author-info">
                                <span className="author-name">{post.author.username}</span>
                                <span className="post-timestamp">{new Date(post.createdAt).toLocaleDateString()}</span>
                            </div>
                        </div>
                        {/* Edit and Delete Buttons */}
                        {post.author._id === MOCK_USER._id && (
                            <div className="post-card-actions">
                                <button className="card-action-btn" title="Edit">‚úèÔ∏è</button>
                                <button className="card-action-btn" title="Delete" onClick={() => handleDeletePost(post._id)}>üóëÔ∏è</button>
                            </div>
                        )}
                    </div>
                    {/* ... rest of the post card JSX ... */}
                    <div className="post-content">
                      <h4 className="post-title">{post.title}</h4>
                      <p className="post-text">{post.content}</p>
                    </div>
                    {/* ... actions like like, comment, share ... */}
                  </div>
                ))}
              </div>
            ) : (
              <div className="empty-state">
                <div className="empty-icon">üì≠</div>
                <h3 className="empty-title">No posts found</h3>
                <p className="empty-text">Try changing your filters or be the first to share!</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Community;
