import React, { useState, useEffect } from 'react';
import './Affirmations.css';

const Affirmations = () => {
  const [affirmations, setAffirmations] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [customAffirmation, setCustomAffirmation] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [loading, setLoading] = useState(true);

  const categories = [
    { id: 'all', name: 'All Affirmations', icon: '‚ú®' },
    { id: 'self-love', name: 'Self Love', icon: 'üíñ' },
    { id: 'confidence', name: 'Confidence', icon: 'üí™' },
    { id: 'gratitude', name: 'Gratitude', icon: 'üôè' },
    { id: 'success', name: 'Success', icon: 'üèÜ' },
    { id: 'health', name: 'Health & Wellness', icon: 'üåø' },
    { id: 'relationships', name: 'Relationships', icon: 'ü§ù' },
    { id: 'creativity', icon: 'üé®', name: 'Creativity' }
  ];

  const allAffirmations = [
    // Self Love
    { id: 1, text: "I am worthy of love, respect, and happiness", category: "self-love", author: "Daily Inspiration" },
    { id: 2, text: "I accept myself exactly as I am", category: "self-love", author: "Mindful Living" },
    { id: 3, text: "I am enough, just as I am", category: "self-love", author: "Self Care Daily" },
    { id: 4, text: "I choose to love and accept myself unconditionally", category: "self-love", author: "Wellness Journey" },
    
    // Confidence
    { id: 5, text: "I am confident and capable of achieving my goals", category: "confidence", author: "Success Mindset" },
    { id: 6, text: "I trust in my abilities and inner wisdom", category: "confidence", author: "Empowerment Daily" },
    { id: 7, text: "I am strong, resilient, and unstoppable", category: "confidence", author: "Motivation Hub" },
    { id: 8, text: "I believe in myself and my potential", category: "confidence", author: "Confidence Builder" },
    
    // Gratitude
    { id: 9, text: "I am grateful for all the blessings in my life", category: "gratitude", author: "Gratitude Practice" },
    { id: 10, text: "Every day brings new opportunities to be thankful", category: "gratitude", author: "Thankful Heart" },
    { id: 11, text: "I appreciate the abundance that surrounds me", category: "gratitude", author: "Abundance Mindset" },
    { id: 12, text: "I find joy in the simple moments of life", category: "gratitude", author: "Joyful Living" },
    
    // Success
    { id: 13, text: "I am successful in all areas of my life", category: "success", author: "Success Coach" },
    { id: 14, text: "I attract opportunities that lead to success", category: "success", author: "Opportunity Seeker" },
    { id: 15, text: "I am a magnet for abundance and prosperity", category: "success", author: "Prosperity Mindset" },
    { id: 16, text: "I achieve my goals with ease and grace", category: "success", author: "Goal Achiever" },
    
    // Health & Wellness
    { id: 17, text: "I am healthy, strong, and full of energy", category: "health", author: "Wellness Warrior" },
    { id: 18, text: "My body is a temple and I treat it with love", category: "health", author: "Body Love" },
    { id: 19, text: "I choose nourishing thoughts and actions", category: "health", author: "Mindful Health" },
    { id: 20, text: "I am in perfect harmony with my mind and body", category: "health", author: "Holistic Living" },
    
    // Relationships
    { id: 21, text: "I attract loving and supportive relationships", category: "relationships", author: "Love Magnet" },
    { id: 22, text: "I communicate with love and understanding", category: "relationships", author: "Communication Expert" },
    { id: 23, text: "I am surrounded by people who uplift me", category: "relationships", author: "Positive Circle" },
    { id: 24, text: "I give and receive love freely", category: "relationships", author: "Love Giver" },
    
    // Creativity
    { id: 25, text: "I am a creative being with unlimited potential", category: "creativity", author: "Creative Soul" },
    { id: 26, text: "My imagination knows no bounds", category: "creativity", author: "Imagination Master" },
    { id: 27, text: "I express myself authentically and beautifully", category: "creativity", author: "Authentic Expression" },
    { id: 28, text: "I am inspired and inspiring to others", category: "creativity", author: "Inspiration Source" }
  ];

  useEffect(() => {
    // Load favorites from localStorage
    const savedFavorites = JSON.parse(localStorage.getItem('favoriteAffirmations') || '[]');
    setFavorites(savedFavorites);
    
    setTimeout(() => {
      setAffirmations(allAffirmations);
      setLoading(false);
    }, 1000);
  }, []);

  useEffect(() => {
    localStorage.setItem('favoriteAffirmations', JSON.stringify(favorites));
  }, [favorites]);

  const toggleFavorite = (affirmationId) => {
    setFavorites(prev => 
      prev.includes(affirmationId) 
        ? prev.filter(id => id !== affirmationId)
        : [...prev, affirmationId]
    );
  };

  const addCustomAffirmation = (e) => {
    e.preventDefault();
    if (customAffirmation.trim()) {
      const newAffirmation = {
        id: Date.now(),
        text: customAffirmation.trim(),
        category: 'custom',
        author: 'You',
        isCustom: true
      };
      setAffirmations([newAffirmation, ...affirmations]);
      setCustomAffirmation('');
    }
  };

  const deleteCustomAffirmation = (affirmationId) => {
    setAffirmations(affirmations.filter(a => a.id !== affirmationId));
    setFavorites(prev => prev.filter(id => id !== affirmationId));
  };

  const filteredAffirmations = selectedCategory === 'all' 
    ? affirmations 
    : affirmations.filter(a => a.category === selectedCategory);

  const favoriteAffirmations = affirmations.filter(a => favorites.includes(a.id));

  if (loading) {
    return (
      <div className="affirmations-container">
        <div className="loading-container">
          <div className="spinner"></div>
          <p className="loading-text">Loading affirmations...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="affirmations-container">
      <div className="affirmations-header">
        <div className="header-content">
          <h1 className="affirmations-title font-handwriting">
            <span className="title-icon">‚ú®</span>
            Daily Affirmations
          </h1>
          <p className="affirmations-subtitle">
            Nurture your mind with positive thoughts and transform your reality
          </p>
        </div>
        <div className="header-stats">
          <div className="stat-card">
            <span className="stat-icon">üíù</span>
            <div className="stat-info">
              <span className="stat-number">{favorites.length}</span>
              <span className="stat-label">Favorites</span>
            </div>
          </div>
          <div className="stat-card">
            <span className="stat-icon">üìù</span>
            <div className="stat-info">
              <span className="stat-number">{affirmations.length}</span>
              <span className="stat-label">Total</span>
            </div>
          </div>
        </div>
      </div>

      <div className="affirmations-content">
        <div className="affirmations-sidebar">
          <div className="sidebar-section">
            <h3 className="section-title">Categories</h3>
            <div className="category-list">
              {categories.map(category => (
                <button
                  key={category.id}
                  className={`category-item ${selectedCategory === category.id ? 'active' : ''}`}
                  onClick={() => setSelectedCategory(category.id)}
                >
                  <span className="category-icon">{category.icon}</span>
                  <span className="category-name">{category.name}</span>
                </button>
              ))}
            </div>
          </div>

          <div className="sidebar-section">
            <h3 className="section-title">Quick Actions</h3>
            <div className="quick-actions">
              <button 
                className="quick-action-btn"
                onClick={() => setSelectedCategory('all')}
              >
                <span className="action-icon">üìñ</span>
                View All
              </button>
              <button 
                className="quick-action-btn"
                onClick={() => {
                  const randomAffirmation = affirmations[Math.floor(Math.random() * affirmations.length)];
                  alert(`Today's Affirmation: "${randomAffirmation.text}"`);
                }}
              >
                <span className="action-icon">üé≤</span>
                Random Pick
              </button>
              <button 
                className="quick-action-btn"
                onClick={() => {
                  const randomFavorites = favorites.length > 0 
                    ? favorites[Math.floor(Math.random() * favorites.length)]
                    : null;
                  if (randomFavorites) {
                    const affirmation = affirmations.find(a => a.id === randomFavorites);
                    alert(`Your Favorite: "${affirmation.text}"`);
                  } else {
                    alert('Add some favorites first!');
                  }
                }}
              >
                <span className="action-icon">‚≠ê</span>
                Random Favorite
              </button>
            </div>
          </div>

          <div className="sidebar-section">
            <h3 className="section-title">Tips</h3>
            <div className="tips">
              <div className="tip-item">
                <span className="tip-icon">üåÖ</span>
                <span className="tip-text">Repeat affirmations in the morning</span>
              </div>
              <div className="tip-item">
                <span className="tip-icon">üîÅ</span>
                <span className="tip-text">Say them out loud with conviction</span>
              </div>
              <div className="tip-item">
                <span className="tip-icon">üí≠</span>
                <span className="tip-text">Visualize them as already true</span>
              </div>
              <div className="tip-item">
                <span className="tip-icon">üì±</span>
                <span className="tip-text">Set reminders throughout the day</span>
              </div>
            </div>
          </div>
        </div>

        <div className="affirmations-main">
          <div className="create-affirmation-section">
            <div className="create-affirmation-card">
              <h3 className="create-affirmation-title">Create Your Own</h3>
              <form onSubmit={addCustomAffirmation} className="create-affirmation-form">
                <textarea
                  placeholder="Write your personal affirmation here..."
                  value={customAffirmation}
                  onChange={(e) => setCustomAffirmation(e.target.value)}
                  className="affirmation-input"
                  rows={3}
                  maxLength={200}
                />
                <div className="form-actions">
                  <span className="char-count">
                    {customAffirmation.length}/200 characters
                  </span>
                  <button type="submit" className="submit-affirmation-btn">
                    <span className="btn-icon">‚ú®</span>
                    Add Affirmation
                  </button>
                </div>
              </form>
            </div>
          </div>

          {favorites.length > 0 && (
            <div className="favorites-section">
              <h3 className="section-title">
                <span className="title-icon">‚≠ê</span>
                Your Favorites
              </h3>
              <div className="affirmations-grid">
                {favoriteAffirmations.map(affirmation => (
                  <div key={affirmation.id} className="affirmation-card favorite">
                    <div className="affirmation-content">
                      <p className="affirmation-text">{affirmation.text}</p>
                      <span className="affirmation-author">‚Äî {affirmation.author}</span>
                    </div>
                    <div className="affirmation-actions">
                      <button
                        className="action-btn favorite-btn active"
                        onClick={() => toggleFavorite(affirmation.id)}
                        title="Remove from favorites"
                      >
                        <span className="action-icon">‚≠ê</span>
                      </button>
                      {affirmation.isCustom && (
                        <button
                          className="action-btn delete-btn"
                          onClick={() => deleteCustomAffirmation(affirmation.id)}
                          title="Delete affirmation"
                        >
                          <span className="action-icon">üóëÔ∏è</span>
                        </button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          <div className="all-affirmations-section">
            <div className="section-header">
              <h3 className="section-title">
                {selectedCategory === 'all' ? 'All Affirmations' : categories.find(c => c.id === selectedCategory)?.name}
              </h3>
              <span className="affirmations-count">{filteredAffirmations.length} affirmations</span>
            </div>

            <div className="affirmations-grid">
              {filteredAffirmations.map(affirmation => (
                <div key={affirmation.id} className="affirmation-card">
                  <div className="affirmation-content">
                    <p className="affirmation-text">{affirmation.text}</p>
                    <span className="affirmation-author">‚Äî {affirmation.author}</span>
                  </div>
                  <div className="affirmation-actions">
                    <button
                      className={`action-btn favorite-btn ${favorites.includes(affirmation.id) ? 'active' : ''}`}
                      onClick={() => toggleFavorite(affirmation.id)}
                      title={favorites.includes(affirmation.id) ? 'Remove from favorites' : 'Add to favorites'}
                    >
                      <span className="action-icon">
                        {favorites.includes(affirmation.id) ? '‚≠ê' : '‚òÜ'}
                      </span>
                    </button>
                    {affirmation.isCustom && (
                      <button
                        className="action-btn delete-btn"
                        onClick={() => deleteCustomAffirmation(affirmation.id)}
                        title="Delete affirmation"
                      >
                        <span className="action-icon">üóëÔ∏è</span>
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>

            {filteredAffirmations.length === 0 && (
              <div className="empty-state">
                <div className="empty-icon">üì≠</div>
                <h3 className="empty-title">No affirmations found</h3>
                <p className="empty-text">
                  Try changing your category or create your own affirmation!
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Affirmations;
