const Footer = () => {
  return (
    <footer className="bg-pink-100 text-pink-700 py-6 mt-10">
      <div className="max-w-5xl mx-auto px-4 flex flex-col md:flex-row justify-between items-center gap-4">
        {/* Left side */}
        <div className="text-center md:text-left font-raleway">
          <div className="
    text-5xl mb-4 text-center font-bold cursor-pointer
    transition-transform duration-700 ease-in-out
    transform hover:scale-105
  "
  style={{
    fontFamily: "'Irish Grover', system-ui",
    color: '#FF69B4', // pastel baby pink
    WebkitTextStroke: '0.2px #228B22', // soft pink border/stroke
    textShadow: `
      0 0 4px rgba(248, 204, 229, 0.8),
      0 0 10px rgba(248, 204, 229, 0.6),
      0 0 20px rgba(248, 204, 229, 0.4)
    `,
    marginTop: '30px',
    transition: 'filter 0.7s ease-in-out, transform 0.7s ease-in-out',
  }}
  onMouseEnter={(e) => {
    e.currentTarget.style.filter = 'brightness(1.2) saturate(1.3)';
  }}
  onMouseLeave={(e) => {
    e.currentTarget.style.filter = 'brightness(1) saturate(1)';
  }}>
          SafeSpace
        </div>
          <p className="text-xs text-pink-500">
            Made with ðŸ’– for your emotional well-being.
          </p>
        </div>

        {/* Right side */}
        <div className="flex space-x-4">
          <a
            href="/privacy-policy"
            className="hover:text-pink-500 transition text-sm underline decoration-pink-300"
          >
            Privacy Policy
          </a>
          <a
            href="/terms-of-service"
            className="hover:text-pink-500 transition text-sm underline decoration-pink-300"
          >
            Terms of Service
          </a>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
