import { motion } from "framer-motion";

const features = [
  "ðŸ§  AI-powered Emotion Insights",
  "ðŸ“ˆ Mood Trend Visualization",
  "âœï¸ Daily Journaling",
  "ðŸ” Private & Secure",
  "ðŸŽ¨ Calming UI & Experience",
  "ðŸ’¬ Emotional Support from SereneAI",
];

const Home = () => {
  return (
    <div className="flex flex-col items-center justify-center bg-transparent font-figtree py-10">
      <div className="relative w-full max-w-4xl min-h-[600px] rounded-3xl shadow-2xl border border-pink-100 overflow-hidden bg-white bg-opacity-90 p-10">
        {/* notebook lines */}
        <div className="absolute inset-0 bg-[linear-gradient(to_bottom,transparent_95%,#e5e7eb_95%)] bg-[length:100%_2.5rem] z-0 pointer-events-none"></div>

        {/* content */}
        <div className="relative z-10">
          <h1 className="text-5xl font-sacramento text-pink-600 mb-6 text-center">
            MoodJournal AI
          </h1>
          <p className="text-lg text-gray-700 mb-6 font-raleway text-center max-w-xl mx-auto">
            Your personal AI-powered diary for emotional clarity, memories, and reflection.
          </p>

          {/* Polaroid style images */}
          <div className="flex flex-wrap justify-center gap-6 mb-6">
            <div className="bg-white shadow-lg p-2 rotate-[-6deg]">
              <img src="/polaroid1.jpg" alt="Memory 1" className="w-40 h-40 object-cover" />
              <p className="text-xs text-center mt-1">Day at the park</p>
            </div>
            <div className="bg-white shadow-lg p-2 rotate-3">
              <img src="/polaroid2.jpg" alt="Memory 2" className="w-40 h-40 object-cover" />
              <p className="text-xs text-center mt-1">Cozy evening</p>
            </div>
            <div className="bg-white shadow-lg p-2 rotate-[-3deg]">
              <img src="/polaroid3.jpg" alt="Memory 3" className="w-40 h-40 object-cover" />
              <p className="text-xs text-center mt-1">Sunset walk</p>
            </div>
          </div>

          {/* Features */}
          <motion.ul className="space-y-4 max-w-md mx-auto">
            {features.map((feature, index) => (
              <motion.li
                key={index}
                className="text-base text-left text-indigo-600 font-semibold"
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.3, duration: 0.4 }}
              >
                {feature}
              </motion.li>
            ))}
          </motion.ul>
        </div>
      </div>
    </div>
  );
};

export default Home;
