import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Community.css';

const API_BASE_URL = 'http://localhost:5001/api/community';

const Community = ({ user }) => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState('latest');
  const [filter, setFilter] = useState('all');
  const [newPost, setNewPost] = useState({ title: '', content: '', category: 'general' });
  const [showMyPosts, setShowMyPosts] = useState(false);
  const [error, setError] = useState('');

  // Fetch posts from backend
  const fetchPosts = async (opts = {}) => {
    setLoading(true);
    setError('');
    try {
      const params = new URLSearchParams();
      params.append('sort', sortBy);
      params.append('category', filter);
      if (showMyPosts && user?._id) params.append('authorId', user._id);
      const res = await fetch(`${API_BASE_URL}?${params.toString()}`);
      const data = await res.json();
      if (data.success) setPosts(data.data);
      else setPosts([]);
    } catch (err) {
      setError('Failed to load posts');
      setPosts([]);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchPosts();
    // eslint-disable-next-line
  }, [sortBy, filter, showMyPosts]);

  // Create post
  const handleSubmitPost = async (e) => {
    e.preventDefault();
    if (!user) return alert('Login required');
    if (!newPost.title.trim() || !newPost.content.trim()) return;
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(API_BASE_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(newPost)
      });
      const data = await res.json();
      if (data.success) {
        setPosts(prev => [data.data, ...prev]);
        setNewPost({ title: '', content: '', category: 'general' });
      } else {
        alert(data.message || 'Failed to post');
      }
    } catch (err) {
      alert('Failed to post');
    }
  };

  // Delete post
  const handleDelete = async (postId) => {
    if (!window.confirm('Delete this post?')) return;
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`${API_BASE_URL}/${postId}`, {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}` }
      });
      const data = await res.json();
      if (data.success) setPosts(prev => prev.filter(p => p._id !== postId));
      else alert(data.message || 'Delete failed');
    } catch (err) {
      alert('Delete failed');
    }
  };

  // (Optional) Edit post logic can be added here

  // Like post (example, not full logic)
  const handleLike = async (postId) => {
    if (!user) return alert('Login required');
    const token = localStorage.getItem('token');
    await fetch(`${API_BASE_URL}/${postId}/like`, {
      method: 'PATCH',
      headers: { 'Authorization': `Bearer ${token}` }
    });
    fetchPosts();
  };

  const sortedPosts = [...posts].sort((a, b) => {
    switch (sortBy) {
      case 'latest':
        return new Date(b.timestamp) - new Date(a.timestamp);
      case 'popular':
        return b.likes - a.likes;
      case 'comments':
        return b.comments - a.comments;
      default:
        return 0;
    }
  });

  const filteredPosts = filter === 'all' 
    ? sortedPosts 
    : sortedPosts.filter(post => post.category === filter);

  const categories = [
    { id: 'all', name: 'All Posts', icon: 'üì∞' },
    { id: 'mental-health', name: 'Mental Health', icon: 'üß†' },
    { id: 'gratitude', name: 'Gratitude', icon: 'üôè' },
    { id: 'writing', name: 'Writing Tips', icon: '‚úçÔ∏è' },
    { id: 'lifestyle', name: 'Lifestyle', icon: 'üåü' }
  ];

  if (loading) {
    return (
      <div className="community-container">
        <div className="loading-container">
          <div className="spinner"></div>
          <p className="loading-text">Loading community posts...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="community-container">
      <div className="community-header">
        <div className="header-content">
          <h1 className="community-title font-handwriting">
            <span className="title-icon">üèòÔ∏è</span>
            Community Hub
          </h1>
          <p className="community-subtitle">
            Connect, share, and grow with fellow journalers
          </p>
        </div>
        <div className="header-stats">
          <div className="stat-card">
            <span className="stat-icon">üë•</span>
            <div className="stat-info">
              <span className="stat-number">1,247</span>
              <span className="stat-label">Members</span>
            </div>
          </div>
          <div className="stat-card">
            <span className="stat-icon">üìù</span>
            <div className="stat-info">
              <span className="stat-number">5,892</span>
              <span className="stat-label">Posts</span>
            </div>
          </div>
          <div className="stat-card">
            <span className="stat-icon">üí¨</span>
            <div className="stat-info">
              <span className="stat-number">12,456</span>
              <span className="stat-label">Comments</span>
            </div>
          </div>
        </div>
      </div>

      <div className="community-content">
        <div className="community-sidebar">
          <div className="sidebar-section">
            <h3 className="section-title">Categories</h3>
            <div className="category-list">
              {categories.map(category => (
                <button
                  key={category.id}
                  className={`category-item ${filter === category.id ? 'active' : ''}`}
                  onClick={() => setFilter(category.id)}
                >
                  <span className="category-icon">{category.icon}</span>
                  <span className="category-name">{category.name}</span>
                </button>
              ))}
            </div>
          </div>

          <div className="sidebar-section">
            <h3 className="section-title">Sort By</h3>
            <div className="sort-options">
              <button
                className={`sort-option ${sortBy === 'latest' ? 'active' : ''}`}
                onClick={() => setSortBy('latest')}
              >
                <span className="sort-icon">üïí</span>
                Latest
              </button>
              <button
                className={`sort-option ${sortBy === 'popular' ? 'active' : ''}`}
                onClick={() => setSortBy('popular')}
              >
                <span className="sort-icon">üî•</span>
                Popular
              </button>
              <button
                className={`sort-option ${sortBy === 'comments' ? 'active' : ''}`}
                onClick={() => setSortBy('comments')}
              >
                <span className="sort-icon">üí¨</span>
                Most Comments
              </button>
            </div>
          </div>

          <div className="sidebar-section">
            <h3 className="section-title">Community Guidelines</h3>
            <div className="guidelines">
              <div className="guideline-item">
                <span className="guideline-icon">ü§ù</span>
                <span className="guideline-text">Be kind and supportive</span>
              </div>
              <div className="guideline-item">
                <span className="guideline-icon">üîí</span>
                <span className="guideline-text">Respect privacy</span>
              </div>
              <div className="guideline-item">
                <span className="guideline-icon">‚ú®</span>
                <span className="guideline-text">Share positive experiences</span>
              </div>
            </div>
          </div>
        </div>

        <div className="community-main">
          <div className="create-post-section">
            <div className="create-post-card">
              <h3 className="create-post-title">Share Your Story</h3>
              <form onSubmit={handleSubmitPost} className="create-post-form">
                <div className="form-row">
                  <input
                    type="text"
                    placeholder="Post title..."
                    value={newPost.title}
                    onChange={(e) => setNewPost({...newPost, title: e.target.value})}
                    className="post-title-input"
                    maxLength={100}
                  />
                  <select
                    value={newPost.category}
                    onChange={(e) => setNewPost({...newPost, category: e.target.value})}
                    className="category-select"
                  >
                    <option value="general">General</option>
                    <option value="mental-health">Mental Health</option>
                    <option value="gratitude">Gratitude</option>
                    <option value="writing">Writing Tips</option>
                    <option value="lifestyle">Lifestyle</option>
                  </select>
                </div>
                <textarea
                  placeholder="Share your thoughts, experiences, or tips with the community..."
                  value={newPost.content}
                  onChange={(e) => setNewPost({...newPost, content: e.target.value})}
                  className="post-content-input"
                  rows={4}
                  maxLength={500}
                />
                <div className="form-actions">
                  <span className="char-count">
                    {newPost.content.length}/500 characters
                  </span>
                  <button type="submit" className="submit-post-btn">
                    <span className="btn-icon">üì§</span>
                    Share Post
                  </button>
                </div>
              </form>
            </div>
          </div>

          <div className="my-posts-toggle" style={{ marginBottom: 16 }}>
            <button
              className={`my-posts-btn${showMyPosts ? ' active' : ''}`}
              onClick={() => setShowMyPosts(v => !v)}
              disabled={!user}
            >
              {showMyPosts ? 'Show All Posts' : 'My Posts'}
            </button>
          </div>

          <div className="posts-section">
            <div className="posts-header">
              <h3 className="posts-title">
                {filter === 'all' ? 'All Posts' : categories.find(c => c.id === filter)?.name}
              </h3>
              <span className="posts-count">{filteredPosts.length} posts</span>
            </div>

            <div className="posts-list">
              {filteredPosts.map(post => (
                <div key={post._id} className="post-card">
                  <div className="post-header">
                    <div className="post-author">
                      <div className="author-avatar">{post.avatar}</div>
                      <div className="author-info">
                        <span className="author-name">{post.author}</span>
                        <span className="post-timestamp">{post.timestamp}</span>
                      </div>
                    </div>
                    <div className="post-category">
                      <span className="category-badge">
                        {categories.find(c => c.id === post.category)?.icon}
                        {categories.find(c => c.id === post.category)?.name}
                      </span>
                    </div>
                  </div>

                  <div className="post-content">
                    <h4 className="post-title">{post.title}</h4>
                    <p className="post-text">{post.content}</p>
                  </div>

                  <div className="post-actions">
                    <button
                      className={`action-btn like-btn ${post.isLiked ? 'liked' : ''}`}
                      onClick={() => handleLike(post._id)}
                    >
                      <span className="action-icon">
                        {post.isLiked ? '‚ù§Ô∏è' : 'ü§ç'}
                      </span>
                      <span className="action-count">{post.likeCount || 0}</span>
                    </button>
                    <button className="action-btn comment-btn">
                      <span className="action-icon">üí¨</span>
                      <span className="action-count">{post.commentCount || 0}</span>
                    </button>
                    <button className="action-btn share-btn">
                      <span className="action-icon">üì§</span>
                      <span className="action-text">Share</span>
                    </button>
                    {user && post.author && post.author._id === user._id && (
                      <button className="action-btn delete-btn" onClick={() => handleDelete(post._id)}>
                        <span className="action-icon">üóëÔ∏è</span>
                        <span className="action-text">Delete</span>
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>

            {filteredPosts.length === 0 && (
              <div className="empty-state">
                <div className="empty-icon">üì≠</div>
                <h3 className="empty-title">No posts found</h3>
                <p className="empty-text">
                  Try changing your filter or be the first to share something!
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Community;
