import { motion } from "framer-motion";
import { useState } from "react";

const features = [
  "🧠 AI-powered Emotion Insights",
  "📈 Mood Trend Visualization",
  "✍️ Daily Journaling",
  "🔐 Private & Secure",
  "🎨 Calming UI & Experience",
  "💬 Emotional Support from SereneAI",
];

const Home = () => {
  const [currentPage, setCurrentPage] = useState(0);

  const nextPage = () => {
    setCurrentPage((prev) => (prev + 1) % features.length);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-pink-50 font-figtree py-10">
      <div className="relative w-full max-w-5xl h-[600px] flex shadow-xl rounded-3xl overflow-hidden">
        {/* Left Page */}
        <div className="relative flex-1 bg-white bg-opacity-90 border-r border-pink-200">
          <div className="absolute inset-0 bg-[linear-gradient(to_bottom,transparent_95%,#e5e7eb_95%)] bg-[size:100%_2.5rem]" />
          <div className="p-10">
            <h1 className="text-4xl font-sacramento text-pink-500 mb-6 text-center">
              MoodJournal AI
            </h1>
            <p className="text-base text-gray-600 text-center">
              Emotional clarity & calm reflection.
            </p>
            <div className="mt-10">
              <img
                src="/polaroid1.jpg"
                alt="polaroid"
                className="mx-auto w-40 h-40 object-cover shadow-md rotate-[-5deg]"
              />
              <p className="text-center text-xs mt-2">Day at the park</p>
            </div>
          </div>
        </div>

        {/* Right Page */}
        <div className="relative flex-1 bg-white bg-opacity-90">
          <div className="absolute inset-0 bg-[linear-gradient(to_bottom,transparent_95%,#e5e7eb_95%)] bg-[size:100%_2.5rem]" />
          
          {/* Page flip animation */}
          <motion.div
            key={currentPage}
            initial={{ rotateY: 0 }}
            animate={{ rotateY: 180 }}
            exit={{ rotateY: 0 }}
            transition={{ duration: 1, ease: "easeInOut" }}
            className="absolute inset-0 bg-white bg-opacity-90 border-l border-pink-200 z-10 origin-left"
            style={{ transformStyle: "preserve-3d" }}
            onAnimationComplete={nextPage}
          >
            <div className="absolute inset-0 p-10 flex flex-col justify-center items-center">
              <h2 className="text-2xl text-indigo-600 font-semibold">
                {features[currentPage]}
              </h2>
            </div>
            {/* Back of page */}
            <div
              className="absolute inset-0 bg-white bg-opacity-80 border-l border-pink-200"
              style={{
                transform: "rotateY(180deg)",
                backfaceVisibility: "hidden",
              }}
            />
          </motion.div>

          {/* Static page behind */}
          <div className="absolute inset-0 p-10 flex flex-col justify-center items-center z-0">
            <h2 className="text-xl text-indigo-400">Waiting for next page…</h2>
          </div>
        </div>

        {/* Spiral */}
        <div className="absolute left-[-30px] top-0 h-full flex flex-col justify-around z-20">
          {[...Array(10)].map((_, i) => (
            <div
              key={i}
              className="w-3 h-3 rounded-full bg-gray-400 border border-gray-500"
            />
          ))}
        </div>
      </div>

      <button
        onClick={nextPage}
        className="mt-8 px-6 py-3 bg-pink-500 text-white rounded-full shadow hover:bg-pink-600 transition"
      >
        Flip Page
      </button>
    </div>
  );
};

export default Home;
