import React, { useState } from 'react';
import { Bar, Line, Bubble } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend);

const Dashboard = () => {
  const [clicked, setClicked] = useState({});
  const [currentMonth, setCurrentMonth] = useState(new Date('2025-07-05')); // Start with July 2025

  // Updated moodData with 7 emotions/moods and dummy data
  const moodData = {
    labels: ['Happy', 'Sad', 'Excited', 'Calm', 'Anxious', 'Joyful', 'Tired'],
    datasets: [
      { label: 'Mood Intensity', data: [25, 10, 18, 15, 12, 20, 8], backgroundColor: 'rgba(255, 182, 193, 0.6)' },
    ],
  };

  const lineData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      { label: 'Mood Trend', data: [5, 10, 12, 15, 18, 20], borderColor: 'rgba(255, 182, 193, 0.8)', fill: false },
      { label: 'Energy', data: [3, 7, 10, 14, 18, 25], borderColor: 'rgba(255, 105, 180, 0.8)', fill: false },
      { label: 'Productivity', data: [2, 4, 6, 8, 10, 12], borderColor: 'rgba(255, 160, 180, 0.8)', fill: false },
    ],
  };

  const bubbleData = {
    datasets: [
      {
        label: 'Your Days So Far',
        data: [
          { x: 10, y: 20, r: 15 },
          { x: 20, y: 30, r: 20 },
          { x: 30, y: 40, r: 25 },
          { x: 40, y: 50, r: 20 },
          { x: 50, y: 60, r: 15 },
          { x: 60, y: 70, r: 10 },
          { x: 70, y: 80, r: 15 },
        ],
        backgroundColor: 'rgba(255, 182, 193, 0.6)',
      },
    ],
  };

  const activityData = {
    labels: ['Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec', 'Jan', 'Feb', 'Mar'],
    datasets: [
      {
        label: 'Activity',
        data: [1, 2, 3, 4, 5, 6, 7, 8, 7, 6, 5, 4],
        backgroundColor: (ctx) => {
          const value = ctx.dataset.data[ctx.dataIndex];
          return value > 6 ? 'rgba(255, 105, 180, 0.7)' : value > 3 ? 'rgba(255, 160, 180, 0.7)' : 'rgba(255, 192, 203, 0.7)';
        },
        borderColor: 'rgba(255, 105, 180, 1)',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: { legend: { position: 'top' }, title: { display: false } },
    animation: { duration: 1000, easing: 'easeInOutQuad' },
    scales: { y: { beginAtZero: true } },
  };

  const cardClass = "bg-pink-50 p-6 rounded-3xl shadow hover:shadow-xl transition-all duration-300 w-full";

  // Generate calendar days for the current month
  const getCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const days = [];
    const prevMonthDays = new Date(year, month, 0).getDate();

    // Start from the previous month's last day to align with the first day of the week
    for (let i = firstDay.getDay() - 1; i >= 0; i--) {
      days.push({ day: prevMonthDays - i, isCurrentMonth: false });
    }

    // Current month's days
    for (let day = 1; day <= lastDay.getDate(); day++) {
      days.push({ day, isCurrentMonth: true });
    }

    // Fill remaining days with next month
    while (days.length < 42) { // 6 weeks max
      days.push({ day: days.length - lastDay.getDate(), isCurrentMonth: false });
    }

    return days;
  };

  const calendarDays = getCalendarDays();
  const monthName = currentMonth.toLocaleString('default', { month: 'long' });
  const year = currentMonth.getFullYear();

  const handlePrevMonth = () => {
    setCurrentMonth(new Date(currentMonth.setMonth(currentMonth.getMonth() - 1)));
  };

  const handleNextMonth = () => {
    setCurrentMonth(new Date(currentMonth.setMonth(currentMonth.getMonth() + 1)));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 via-pink-100 to-pink-200 p-8 grid gap-6 grid-cols-1 md:grid-cols-2">
      {/* First Half: Two Graphs */}
      <div className={`${cardClass}`} onClick={() => setClicked({ mood: !clicked.mood })}>
        <h2 className="text-xl font-bold text-pink-700 mb-4 font-sacramento text-center">ðŸŒ¸ Mood Past 7 Days</h2>
        <Bar data={moodData} options={chartOptions} />
      </div>

      <div className={`${cardClass}`} onClick={() => setClicked({ bubble: !clicked.bubble })}>
        <h2 className="text-xl font-bold text-pink-600 mb-4 font-sacramento text-center">ðŸ’– Your Days So Far</h2>
        <Bubble data={bubbleData} options={chartOptions} />
      </div>

      {/* Second Half: Two Graphs and Marua */}
      <div className={`${cardClass}`} onClick={() => setClicked({ line: !clicked.line })}>
        <h2 className="text-xl font-bold text-pink-700 mb-4 font-sacramento text-center">ðŸŒ· Your Mood (6 Months)</h2>
        <Line data={lineData} options={chartOptions} />
      </div>

      <div className={`${cardClass} flex items-center justify-center`}>
        <img
          src="https://via.placeholder.com/80"
          alt="Marua"
          className="rounded-full border-4 border-pink-300 shadow mr-4"
        />
        <div>
          <h2 className="text-xl font-bold text-pink-700 font-sacramento">Marua</h2>
          <div className="bg-pink-400/80 px-3 py-1 rounded-full text-white mt-2 shadow-sm text-center">
            How are you feeling today?
          </div>
        </div>
      </div>

      {/* Activity & Streak with Calendar */}
      <div className={`${cardClass} md:col-span-2`}>
        <h2 className="text-xl font-bold text-pink-700 mb-4 font-sacramento text-center">ðŸŒ¸ Activity & Streak</h2>
        <div className="flex justify-around text-pink-600 text-sm flex-wrap mb-4">
          <p>Total Active Days: <strong>12</strong></p>
          <p>Happy Days: <strong>6</strong></p>
          <p>Sad Days: <strong>6</strong></p>
          <p>Most Common Emotion: <strong>ðŸ˜Š Happy</strong></p>
          <p>Current Streak: <strong>8</strong></p>
          <p>Max Streak: <strong>8</strong></p>
        </div>
        <Bar
          data={activityData}
          options={{
            ...chartOptions,
            animation: {
              duration: clicked.activity ? 0 : 1000,
              easing: 'easeOutCubic',
            },
          }}
        />
        <p className="text-pink-500 text-xs mt-2 text-center cursor-pointer">ðŸŒ¸ Learn how we count contributions</p>

        {/* Calendar */}
        <div className="mt-6 bg-pink-100 p-4 rounded-2xl shadow">
          <div className="flex justify-between items-center mb-4">
            <button
              onClick={handlePrevMonth}
              className="bg-pink-300 text-white px-3 py-1 rounded-full hover:bg-pink-400"
            >
              ðŸ‘ˆ
            </button>
            <h3 className="text-2xl font-sacramento text-pink-700">{year} {monthName}</h3>
            <button
              onClick={handleNextMonth}
              className="bg-pink-300 text-white px-3 py-1 rounded-full hover:bg-pink-400"
            >
