import './App.css';
import './output.css';
import { motion } from "framer-motion";

const features = [
  "🧠 AI-powered Emotion Insights",
  "📈 Mood Trend Visualization",
  "✍️ Daily Journaling",
  "🔐 Private & Secure",
  "🎨 Calming UI & Experience",
  "💬 Emotional Support from SereneAI",
];

const Landing = () => {
  return (
    <div className="flex items-center justify-center min-h-screen bg-transparent font-figtree">
      <div className="relative w-full max-w-md rounded-2xl shadow-xl border border-pink-100 overflow-hidden bg-white bg-opacity-80">
        {/* Lined notebook effect */}
        <div className="absolute inset-0 bg-[linear-gradient(to_bottom,transparent_94%,#e5e7eb_94%)] bg-[length:100%_2rem] z-0 pointer-events-none"></div>

        {/* Content */}
        <div className="relative px-6 py-10 z-10">
          <h1 className="text-4xl font-sacramento text-pink-600 mb-4 text-center">MoodJournal AI</h1>
          <p className="text-base text-gray-700 mb-8 font-raleway text-center">
            Your personal AI-powered diary for emotional clarity and calm reflection.
          </p>

          <motion.ul className="space-y-5">
            {features.map((feature, index) => (
              <motion.li
                key={index}
                className="text-base text-left text-indigo-600 font-semibold"
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.3, duration: 0.4 }}
              >
                {feature}
              </motion.li>
            ))}
          </motion.ul>
        </div>
      </div>
    </div>
  );
};

export default Landing;
