import './App.css';
import './output.css';

const Login = ({ onLogin }) => {
  return (
    <div
      className="min-h-screen flex items-center justify-center p-4"
      style={{
        backgroundImage: `url('bg.png')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
      }}
    >
      <div
        className="bg-pink-100 rounded-2xl shadow-xl p-8 w-full max-w-md"
        style={{
          // remove marginTop to keep it centered
        }}
      >
        <h1
          className="text-4xl text-pink-600 text-center mb-6"
          style={{ fontFamily: "'Handlee', cursive" }}
        >
          ðŸŒ¸ Welcome Back!
        </h1>

        <form className="space-y-4">
          <input
            type="text"
            placeholder="Username"
            className="w-full p-4 rounded-xl border border-pink-200 focus:outline-none focus:ring-2 focus:ring-green-300 text-lg"
            style={{ fontFamily: "'Nunito', sans-serif" }}
          />
          <input
            type="password"
            placeholder="Password"
            className="w-full p-4 rounded-xl border border-pink-200 focus:outline-none focus:ring-2 focus:ring-pink-300 text-lg"
            style={{ fontFamily: "'Nunito', sans-serif" }}
          />
          <button
            onClick={(e) => {
              e.preventDefault();
              onLogin(); // Call the callback to set isLoggedIn to true
              window.location.href = '/dashboard'; // Redirect after setting state
            }}
            type="submit"
            className="w-full bg-gradient-to-r from-pink-300 to-green-300 text-pink-900 font-bold py-3 rounded-full shadow-md hover:scale-105 transform transition-all duration-300 ease-in-out text-xl"
            style={{ fontFamily: "'Handlee', cursive", marginTop: '30px' }}
          >
            ðŸŒˆ Login âœ¨
          </button>
        </form>

        <p
          className="mt-4 text-center"
          style={{
            fontFamily: "'Nunito', sans-serif",
            color: '#6b7280',
            marginTop: '20px',
          }}
        >
          Don't have an account?{' '}
          <a href="/signin" className="text-pink-600 hover:underline">
            Sign up
          </a>
        </p>
      </div>
    </div>
  );
};

export default Login;
