// src/ActivityHeatmap.js

import React from 'react';

const ActivityHeatmap = ({ data }) => {
  const today = new Date();
  const daysInYear = 365;
  const startDate = new Date(today.getFullYear(), 0, 1);
  const weekDays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  // Create an array of all days in the last year
  const dates = Array.from({ length: daysInYear }, (_, i) => {
    const date = new Date(startDate);
    date.setDate(startDate.getDate() + i);
    return date;
  });

  const getColor = (count) => {
    if (count === 0) return '#ebedf0'; // Gray
    if (count < 2) return '#f9a8d4'; // Light Pink
    if (count < 4) return '#f472b6'; // Pink
    if (count < 6) return '#ec4899'; // Hot Pink
    return '#db2777'; // Darker Pink
  };

  return (
    <div>
      <div className="activity-heatmap" style={{ gridTemplateColumns: `repeat(${Math.ceil(daysInYear / 7)}, 1fr)` }}>
        {dates.map((date, index) => {
          const dateString = date.toISOString().split('T')[0];
          const count = data[dateString] || 0;
          const color = getColor(count);

          return (
            <div
              key={index}
              className="heatmap-day"
              style={{ backgroundColor: color }}
            >
              <span className="heatmap-tooltip">
                {count} entries on {date.toLocaleDateString()}
              </span>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ActivityHeatmap;
