import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './JournalEntry.css';

const JournalEntry = ({ user }) => {
  const { username } = useParams();
  const [entries, setEntries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('date');
  const [sortOrder, setSortOrder] = useState('desc');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingEntry, setEditingEntry] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    mood: { primary: 'neutral', intensity: 5 },
    tags: [],
    isPrivate: true
  });

  const API_BASE_URL = 'http://localhost:5001/api';

  // Fetch entries
  const fetchEntries = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const params = new URLSearchParams({
        page: currentPage,
        limit: 10,
        search: searchTerm,
        sortBy,
        sortOrder
      });

      const response = await fetch(`${API_BASE_URL}/journal?${params}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setEntries(data.data.entries);
        setTotalPages(data.data.pagination.total);
      } else {
        setError('Failed to fetch entries');
      }
    } catch (error) {
      setError('Network error');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEntries();
  }, [currentPage, searchTerm, sortBy, sortOrder]);

  // Create new entry
  const handleCreateEntry = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_BASE_URL}/journal`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        const data = await response.json();
        setEntries([data.data.entry, ...entries]);
        setShowCreateForm(false);
        setFormData({
          title: '',
          content: '',
          mood: { primary: 'neutral', intensity: 5 },
          tags: [],
          isPrivate: true
        });
        alert('🌸 Entry created successfully!');
      } else {
        const errorData = await response.json();
        alert(errorData.message || 'Failed to create entry');
      }
    } catch (error) {
      alert('Network error');
    }
  };

  // Update entry
  const handleUpdateEntry = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_BASE_URL}/journal/${editingEntry._id}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        const data = await response.json();
        setEntries(entries.map(entry => 
          entry._id === editingEntry._id ? data.data.entry : entry
        ));
        setEditingEntry(null);
        setFormData({
          title: '',
          content: '',
          mood: { primary: 'neutral', intensity: 5 },
          tags: [],
          isPrivate: true
        });
        alert('🌸 Entry updated successfully!');
      } else {
        const errorData = await response.json();
        alert(errorData.message || 'Failed to update entry');
      }
    } catch (error) {
      alert('Network error');
    }
  };

  // Delete entry
  const handleDeleteEntry = async (entryId) => {
    if (!window.confirm('Are you sure you want to delete this entry?')) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_BASE_URL}/journal/${entryId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        setEntries(entries.filter(entry => entry._id !== entryId));
        alert('🌸 Entry deleted successfully!');
      } else {
        const errorData = await response.json();
        alert(errorData.message || 'Failed to delete entry');
      }
    } catch (error) {
      alert('Network error');
    }
  };

  // Check if entry can be edited/deleted
  const canEditEntry = (entry) => {
    const today = new Date();
    const entryDate = new Date(entry.createdAt);
    today.setHours(0, 0, 0, 0);
    entryDate.setHours(0, 0, 0, 0);
    return today.getTime() === entryDate.getTime();
  };

  // Start editing
  const startEditing = (entry) => {
    setEditingEntry(entry);
    setFormData({
      title: entry.title || '',
      content: entry.content || '',
      mood: entry.mood || { primary: 'neutral', intensity: 5 },
      tags: entry.tags || [],
      isPrivate: entry.isPrivate !== undefined ? entry.isPrivate : true
    });
  };

  // Cancel editing
  const cancelEditing = () => {
    setEditingEntry(null);
    setFormData({
      title: '',
      content: '',
      mood: { primary: 'neutral', intensity: 5 },
      tags: [],
      isPrivate: true
    });
  };

  const moodEmojis = {
    happy: '😊',
    sad: '😢',
    excited: '🤩',
    calm: '😌',
    anxious: '😰',
    joyful: '😄',
    tired: '😴',
    neutral: '😐'
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 to-green-50 flex items-center justify-center">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 to-green-50 p-6">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold gradient-text mb-4" style={{ fontFamily: "'Handlee', cursive" }}>
            📝 My Journal Entries
          </h1>
          <p className="text-pink-600 text-lg">
            Welcome back, {user?.username}! 🌸
          </p>
        </div>

        {/* Search and Sort Controls */}
        <div className="card mb-6">
          <div className="flex flex-wrap gap-4 items-center justify-between">
            {/* Search */}
            <div className="flex-1 min-w-64">
              <input
                type="text"
                placeholder="🔍 Search entries..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="input-field"
              />
            </div>

            {/* Sort Controls */}
            <div className="flex gap-2">
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="input-field w-auto"
              >
                <option value="date">📅 Date</option>
                <option value="alphabet">🔤 Alphabet</option>
                <option value="points">⭐ Points</option>
              </select>
              <select
                value={sortOrder}
                onChange={(e) => setSortOrder(e.target.value)}
                className="input-field w-auto"
              >
                <option value="desc">⬇️ Descending</option>
                <option value="asc">⬆️ Ascending</option>
              </select>
            </div>

            {/* Create Button */}
            <button
              onClick={() => setShowCreateForm(true)}
              className="btn-primary"
            >
              ✨ New Entry
            </button>
          </div>
        </div>

        {/* Create/Edit Form */}
        {(showCreateForm || editingEntry) && (
          <div className="card mb-6">
            <h2 className="text-2xl font-bold mb-4 gradient-text">
              {editingEntry ? '✏️ Edit Entry' : '✨ Create New Entry'}
            </h2>
            <form onSubmit={editingEntry ? handleUpdateEntry : handleCreateEntry}>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                <input
                  type="text"
                  placeholder="Entry title..."
                  value={formData.title}
                  onChange={(e) => setFormData({...formData, title: e.target.value})}
                  className="input-field"
                  required
                />
                <select
                  value={formData.mood.primary}
                  onChange={(e) => setFormData({
                    ...formData, 
                    mood: {...formData.mood, primary: e.target.value}
                  })}
                  className="input-field"
                >
                  <option value="neutral">😐 Neutral</option>
                  <option value="happy">😊 Happy</option>
                  <option value="sad">😢 Sad</option>
                  <option value="excited">🤩 Excited</option>
                  <option value="calm">😌 Calm</option>
                  <option value="anxious">😰 Anxious</option>
                  <option value="joyful">😄 Joyful</option>
                  <option value="tired">😴 Tired</option>
                </select>
              </div>
              
              <textarea
                placeholder="Write your thoughts, feelings, and experiences..."
                value={formData.content}
                onChange={(e) => setFormData({...formData, content: e.target.value})}
                className="input-field h-32 resize-none"
                required
              />
              
              <div className="flex gap-4 mt-4">
                <button type="submit" className="btn-primary">
                  {editingEntry ? '💾 Save Changes' : '🌸 Create Entry'}
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setShowCreateForm(false);
                    cancelEditing();
                  }}
                  className="btn-secondary"
                >
                  ❌ Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Entries List */}
        {entries.length === 0 ? (
          <div className="card text-center py-12">
            <div className="text-6xl mb-4">📝</div>
            <h3 className="text-xl font-semibold mb-2">No entries yet</h3>
            <p className="text-pink-600 mb-4">Start your journaling journey today!</p>
            <button
              onClick={() => setShowCreateForm(true)}
              className="btn-primary"
            >
              ✨ Write Your First Entry
            </button>
          </div>
        ) : (
          <div className="space-y-4">
            {entries.map((entry) => (
              <div key={entry._id} className="card hover:shadow-lg transition-all duration-300">
                <div className="flex justify-between items-start mb-4">
                  <div className="flex-1">
                    <h3 className="text-xl font-semibold mb-2">{entry.title}</h3>
                    <div className="flex items-center gap-4 text-sm text-pink-600 mb-2">
                      <span>{moodEmojis[entry.mood?.primary]} {entry.mood?.primary}</span>
                      <span>⭐ {entry.aiAnalysis?.pointsEarned || 0} points</span>
                      <span>📅 {new Date(entry.createdAt).toLocaleDateString()}</span>
                    </div>
                  </div>
                  
                  {/* Action Buttons */}
                  <div className="flex gap-2">
                    {canEditEntry(entry) && (
                      <>
                        <button
                          onClick={() => startEditing(entry)}
                          className="btn-secondary text-sm"
                        >
                          ✏️ Edit
                        </button>
                        <button
                          onClick={() => handleDeleteEntry(entry._id)}
                          className="btn-secondary text-sm bg-red-100 text-red-700 hover:bg-red-200"
                        >
                          🗑️ Delete
                        </button>
                      </>
                    )}
                  </div>
                </div>
                
                <p className="text-gray-700 mb-4 leading-relaxed">{entry.content}</p>
                
                {/* AI Analysis */}
                {entry.aiAnalysis && (
                  <div className="bg-pink-50 p-4 rounded-lg border-l-4 border-pink-300">
                    <h4 className="font-semibold mb-2">🤖 Dr. Luna's Analysis</h4>
                    <p className="text-sm text-gray-600 mb-2">{entry.aiAnalysis.insights}</p>
                    {entry.aiAnalysis.suggestions && entry.aiAnalysis.suggestions.length > 0 && (
                      <div className="mt-2">
                        <p className="text-sm font-medium text-pink-700">Suggestions:</p>
                        <ul className="text-sm text-gray-600 list-disc list-inside">
                          {entry.aiAnalysis.suggestions.map((suggestion, index) => (
                            <li key={index}>{suggestion}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="flex justify-center mt-8">
            <div className="flex gap-2">
              <button
                onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                disabled={currentPage === 1}
                className="btn-secondary disabled:opacity-50"
              >
                ⬅️ Previous
              </button>
              <span className="px-4 py-2 text-pink-600">
                Page {currentPage} of {totalPages}
              </span>
              <button
                onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
                disabled={currentPage === totalPages}
                className="btn-secondary disabled:opacity-50"
              >
                Next ➡️
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default JournalEntry;
