import React, { useState } from 'react';

const Affirmations = () => {
  const [affirmation, setAffirmation] = useState('');
  const [affirmationsList, setAffirmationsList] = useState([
    'I am doing my best and that is enough.',
    'I accept my thoughts and emotions.',
    'Everyone makes mistakes. I can try again.',
    'I am unique and capable of great things.',
    'I can learn and grow every day.',
    'I can achieve my goals.',
    'I will be patient with myself.',
    'I will encourage myself.',
  ]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (affirmation.trim()) {
      setAffirmationsList([...affirmationsList, affirmation.trim()]);
      setAffirmation('');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-pink-100 to-pink-200 p-6 font-nunito">
      <h1 className="text-4xl font-sacramento text-pink-600 text-center mb-8 animate-pulse">üå∏ My Affirmations Board üå∏</h1>

      {/* Add Affirmation Form */}
      <div className="max-w-2xl mx-auto mb-8">
        <form onSubmit={handleSubmit} className="flex space-x-4">
          <input
            type="text"
            value={affirmation}
            onChange={(e) => setAffirmation(e.target.value)}
            placeholder="Add your affirmation..."
            className="flex-1 p-3 rounded-full border-2 border-pink-300 focus:outline-none focus:ring-2 focus:ring-pink-400 text-pink-800 placeholder-pink-400 transition-all duration-300 ease-in-out"
          />
          <button
            type="submit"
            className="bg-gradient-to-r from-pink-300 to-pink-400 text-white font-bold py-3 px-6 rounded-full shadow-md hover:bg-pink-500 hover:scale-110 transition-all duration-300 ease-in-out"
          >
            ‚ú® Add üå∏
          </button>
        </form>
      </div>

      {/* Affirmations Board */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 max-w-5xl mx-auto">
        {affirmationsList.map((item, index) => (
          <div
            key={index}
            className="bg-opacity-90 p-4 rounded-lg shadow-md hover:shadow-lg hover:scale-105 transition-all duration-300 ease-in-out text-center"
            style={{
              backgroundColor: ['#A8B5C7', '#D4A5A5', '#E8C4B0', '#B7D7D8'][index % 4],
            }}
          >
            <div className="flex justify-center mb-2">
              {index % 4 === 0 && <span role="img" aria-label="sun">‚òÄÔ∏è</span>}
              {index % 4 === 1 && <span role="img" aria-label="rainbow">üåà</span>}
              {index % 4 === 2 && <span role="img" aria-label="heart">‚ù§Ô∏è</span>}
              {index % 4 === 3 && <span role="img" aria-label="moon">üåô</span>}
            </div>
            <p className="text-pink-800 font-semibold">{item}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Affirmations;
