import React, { useState, useEffect } from 'react';

const Community = () => {
  const [posts, setPosts] = useState(() => JSON.parse(localStorage.getItem('posts')) || []);
  const [newPostTitle, setNewPostTitle] = useState('');
  const [newPostContent, setNewPostContent] = useState('');
  const [newReplyContent, setNewReplyContent] = useState('');
  const [editPostId, setEditPostId] = useState(null);
  const [editReply, setEditReply] = useState(null);
  const [editText, setEditText] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    localStorage.setItem('posts', JSON.stringify(posts));
  }, [posts]);

  const handleAddPost = (e) => {
    e.preventDefault();
    if (newPostTitle.trim() && newPostContent.trim()) {
      setPosts([{ title: newPostTitle, content: newPostContent, replies: [], id: Date.now(), timestamp: new Date().toISOString() }, ...posts]);
      setNewPostTitle('');
      setNewPostContent('');
    }
  };

  const handleAddReply = (postId) => {
    if (newReplyContent.trim()) {
      setPosts(posts.map(post => 
        post.id === postId ? { ...post, replies: [{ content: newReplyContent, id: Date.now(), timestamp: new Date().toISOString() }, ...post.replies] } : post
      ));
      setNewReplyContent('');
    }
  };

  const handleEditPost = (postId) => {
    setEditPostId(postId);
    const post = posts.find(p => p.id === postId);
    setEditText(post.content);
  };

  const handleSavePost = (postId) => {
    setPosts(posts.map(post => post.id === postId ? { ...post, content: editText.trim() } : post));
    setEditPostId(null);
    setEditText('');
  };

  const handleEditReply = (postId, replyId) => {
    setEditReply({ postId, replyId });
    const post = posts.find(p => p.id === postId);
    const reply = post.replies.find(r => r.id === replyId);
    setEditText(reply.content);
  };

  const handleSaveReply = (postId, replyId) => {
    setPosts(posts.map(post => 
      post.id === postId ? { ...post, replies: post.replies.map(r => r.id === replyId ? { ...r, content: editText.trim() } : r) } : post
    ));
    setEditReply(null);
    setEditText('');
  };

  const handleDeletePost = (postId) => {
    setPosts(posts.filter(p => p.id !== postId));
  };

  const handleDeleteReply = (postId, replyId) => {
    setPosts(posts.map(post => 
      post.id === postId ? { ...post, replies: post.replies.filter(r => r.id !== replyId) } : post
    ));
  };

  const filteredPosts = posts.filter(post =>
    post.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    post.content.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="max-w-4xl mx-auto">
      <h1 className="text-4xl font-sacramento text-pink-600 text-center mb-8 animate-pulse">üå∏ Anonymous Community üå∏</h1>

      {/* Search Bar */}
      <div className="mb-6">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search posts..."
          className="w-full p-3 rounded-full border-2 border-pink-300 focus:outline-none focus:ring-2 focus:ring-pink-400 text-pink-800 placeholder-pink-400 transition-all duration-300 ease-in-out"
        />
      </div>

      {/* Add Post Form */}
      <div className="post-form mb-6 bg-white bg-opacity-90 p-6 rounded-lg shadow-md">
        <input
          type="text"
          value={newPostTitle}
          onChange={(e) => setNewPostTitle(e.target.value)}
          placeholder="Enter post title..."
          className="w-full p-3 mb-4 rounded-lg border-2 border-pink-300 focus:outline-none focus:ring-2 focus:ring-pink-400 text-pink-800"
        />
        <textarea
          value={newPostContent}
          onChange={(e) => setNewPostContent(e.target.value)}
          placeholder="Write your anonymous post..."
          className="w-full p-3 mb-4 h-32 rounded-lg border-2 border-pink-300 focus:outline-none focus:ring-2 focus:ring-pink-400 text-pink-800"
        />
        <button
          onClick={handleAddPost}
          className="bg-gradient-to-r from-pink-300 to-pink-400 text-white font-bold py-3 px-6 rounded-full shadow-md hover:bg-pink-500 hover:scale-110 transition-all duration-300 ease-in-out"
        >
          Post Anonymously üåü
        </button>
      </div>

      {/* Posts Feed */}
      <div className="space-y-6">
        {filteredPosts.map(post => (
          <div key={post.id} className="post bg-white bg-opacity-90 p-6 rounded-lg shadow-md">
            <div className="post-content">
              <strong className="text-pink-600">Anonymous Post:</strong> {post.title} <span className="text-xs text-pink-400">({new Date(post.timestamp).toLocaleString()})</span>
              <p>{editPostId === post.id ? (
                <div>
                  <input
                    type="text"
                    value={editText}
                    onChange={(e) => setEditText(e.target.value)}
                    className="w-full p-2 mt-2 rounded-lg border-2 border-pink-300 text-pink-800"
                  />
                  <button
                    onClick={() => handleSavePost(post.id)}
                    className="mt-2 bg-green-300 text-white font-bold py-1 px-2 rounded-full hover:bg-green-400 text-sm"
                  >
                    Save
                  </button>
                </div>
              ) : post.content}</p>
            </div>
            <div className="post-actions flex justify-end mt-2">
              {editPostId !== post.id && (
                <>
                  <button
                    onClick={() => handleEditPost(post.id)}
                    className="text-blue-500 hover:text-blue-700 text-sm mr-2"
                  >
                    ‚úèÔ∏è Edit
                  </button>
                  <button
                    onClick={() => handleDeletePost(post.id)}
                    className="text-red-500 hover:text-red-700 text-sm"
                  >
                    üóëÔ∏è Delete
                  </button>
                </>
              )}
            </div>
            <div className="replies mt-4 border-l-2 border-pink-300 pl-4">
              {post.replies.map(reply => (
                <div key={reply.id} className="post-content mt-2">
                  <strong className="text-pink-600">Anonymous Reply:</strong> {editReply?.postId === post.id && editReply?.replyId === reply.id ? (
                    <div>
                      <input
                        type="text"
                        value={editText}
                        onChange={(e) => setEditText(e.target.value)}
                        className="w-full p-2 mt-1 rounded-lg border-2 border-pink-300 text-pink-800"
                      />
                      <button
                        onClick={() => handleSaveReply(post.id, reply.id)}
                        className="mt-1 bg-green-300 text-white font-bold py-1 px-2 rounded-full hover:bg-green-400 text-sm"
                      >
                        Save
                      </button>
                    </div>
                  ) : reply.content} <span className="text-xs text-pink-400">({new Date(reply.timestamp).toLocaleString()})</span>
                  <div className="reply-actions flex justify-end mt-1">
                    {!(editReply?.postId === post.id && editReply?.replyId === reply.id) && (
                      <>
                        <button
                          onClick={() => handleEditReply(post.id, reply.id)}
                          className="text-blue-500 hover:text-blue-700 text-sm mr-2"
                        >
                          ‚úèÔ∏è Edit
                        </button>
                        <button
                          onClick={() => handleDeleteReply(post.id, reply.id)}
                          className="text-red-500 hover:text-red-700 text-sm"
                        >
                          üóëÔ∏è Delete
                        </button>
                      </>
                    )}
                  </div>
                </div>
              ))}
              <div className="reply-form mt-2">
                <textarea
                  value={newReplyContent}
                  onChange={(e) => setNewReplyContent(e.target.value)}
                  placeholder="Write a reply..."
                  className="w-full p-2 h-20 rounded-lg border-2 border-pink-300 focus:outline-none focus:ring-2 focus:ring-pink-400 text-pink-800"
                />
                <button
                  onClick={() => handleAddReply(post.id)}
                  className="mt-2 bg-gradient-to-r from-pink-300 to-pink-400 text-white font-bold py-2 px-4 rounded-full shadow-md hover:bg-pink-500 hover:scale-110 transition-all duration-300 ease-in-out"
                >
                  Reply üå∏
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Community;
